void setAccountControls()
{
    boolean enableBusinessRelation = (smmBusRelTable::findByParty(smmLeadTable.Party).RecId != 0);
    boolean enableCustomer         = (CustTable::findByPartyRecId(smmLeadTable.Party).RecId != 0);

    select firstonly dirOrgPersonRelations
        where dirOrgPersonRelations.EntityRefRecId   == smmLeadTable.RecId &&
              dirOrgPersonRelations.EntityRefTableId == smmLeadTable.TableId;

