public boolean setProcessInstance(HierarchyName _oldProcess, HierarchyName _newProcess)
{
    DialogButton    answer = DialogButton::Yes;

    if (_oldProcess && _oldProcess !=_newProcess)
    {
        // Do you want to remove all open activities?
        answer = Box::yesNoCancel("@SYS109839", DialogButton::Cancel);

        if (answer == DialogButton::Cancel)
        {
            return checkFailed("@SYS25904"); //Update has been canceled.
        }
    }

    smmProcessInstance::write(smmLeadTable, _oldProcess, _newProcess, false, answer == DialogButton::Yes);

    return true;
}

public void updateStatus()
{
    smmLeadStatus status = smmLeadTable.Status;

    qualifyButton.enabled(status != smmLeadStatus::Qualified);
    disqualifyButton.enabled((status != smmLeadStatus::NotQualified) && (status != smmLeadStatus::OnHold));
    postponeButton.enabled((status != smmLeadStatus::Qualified) && (status != smmLeadStatus::NotQualified) && (status != smmLeadStatus::OnHold));
    reactivateButton.enabled(status != smmLeadStatus::Open);
}

