public void delete()
{
    boolean ret;

    if (!smmLeadTable.RecId  || (smmEntityDelete && smmLeadTable_ds.anyMarked()))
    {
        ret = true;
    }
    else
    {
        smmEntityDelete = SmmEntityDelete::construct(smmLeadTable, false);
        ret = smmEntityDelete && smmEntityDelete.prompt();
    }

    if (ret)
    {
        ttsbegin;

        if (smmEntityDelete)
        {
            smmEntityDelete.parmCommonTable(smmLeadTable);
            smmEntityDelete.runOperation();
        }

        element.numberSeqFormHandler().formMethodDataSourceDelete();

        super();

        ttscommit;

        DirParty::showHideMessageBar(element,smmLeadTable);
    }
}

public void linkActive()
{
    QueryBuildDataSource    queryBuildDataSource;
    smmResponsibilityGroup  responsibilityGroup;
    Hierarchy               hierarchy;

    element.numberSeqFormHandler().formMethodDataSourceLinkActive();

    // Is form called with a record
    if (element.args() && element.args().record())
    {
        callerRecord = element.args().record();
        switch (callerRecord.TableId)
        {
            // Is the form called from the contact person form
            case tableNum(ContactPerson) :
                queryBuildDataSource = this.query().dataSourceTable(tableNum(DirOrgPersonRelations));
                if (!queryBuildDataSource)
                {
                    queryBuildDataSource = this.query().dataSourceTable(tableNum(smmLeadTable)).addDataSource(tableNum(DirOrgPersonRelations));
                    queryBuildDataSource.relations(true);
                    queryBuildDataSource.joinMode(JoinMode::ExistsJoin);
                    queryBuildDataSource.addDynalink(fieldNum(DirOrgPersonRelations,ContactPersonId), element.args().record(), fieldNum(ContactPerson,ContactPersonId));
                }
                break;

            // Is the form called from the activities form
            case tableNum(smmActivities) :
                smmActivityParentLink::updateQuery(this.query().dataSourceTable(tableNum(smmLeadTable)), element.args().record());
                break;

            // Is the form called from the responsibilities form
            case tableNum(smmResponsibilityGroup) :
                // Get the current selected responsibilities record
                responsibilityGroup  = element.args().record();

                // Update query
                smmResponsibility::updateQuery(this.query(),this.table(),responsibilityGroup.ResponsibilityId);
                break;

            // Is the form called from the process template form
            case tableNum(Hierarchy) :
                hierarchy = element.args().record();
                selectedProcess = hierarchy.Name;
                break;

            case tableNum(smmCampaignTable) :
                queryBuildDataSource = this.query().dataSourceTable(tableNum(smmLeadRelTable));
                if (!queryBuildDataSource)
                {
                    queryBuildDataSource = this.query().dataSourceTable(tableNum(smmLeadTable)).addDataSource(tableNum(smmLeadRelTable));
                    queryBuildDataSource.relations(true);
                    queryBuildDataSource.joinMode(JoinMode::ExistsJoin);
                    queryBuildDataSource.addDynalink(fieldNum(smmLeadRelTable, RefRecId), element.args().record(), fieldNum(smmCampaignTable, RecId));
                    queryBuildDataSource.addRange(fieldNum(smmLeadRelTable, RefTableId)).value(int2str(tableNum(smmCampaignTable)));
                }
                break;
        }
    }

    super();
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        ret = element.numberSeqFormHandler().formMethodDataSourceValidateWrite(ret) && ret;
        ret = ret && partyForm.validateWrite();
    }

    return ret;
}

public void write()
{
    NoYes           newRecord;
    HierarchyName   oldProcess;
    boolean         ret;

    ttsbegin;

    element.numberSeqFormHandler().formMethodDataSourceWrite();

    if (!dirPartyTable.RecId)
    {
        dirPartyTable_ds.write();
    }

    smmLeadTable.Party = dirPartyTable.RecId;
    newRecord = smmLeadTable.RecId ? NoYes::No : NoYes::Yes;
    oldProcess = smmLeadTable.orig().ProcessName;

    super();

    // Qualify process
    ret = element.setProcessInstance(oldProcess, smmLeadTable.ProcessName) ;

    if (!ret)
    {
        ttsabort;
        smmLeadTable_ds.reread();
        return;
    }

    // If called from other form and creating a new record
    if (callerRecord && newRecord)
    {
        // Copy associations for the caller record if the caller is not smmLeadsListPage
        if (element.args().caller())
        {
            smmCreateEntity::createLinkCommon(callerRecord, smmLeadTable);
        }
    }
    if (newRecord)
    {
        DirParty::showHideMessageBar(element,smmLeadTable);
        element.args().record(smmLeadTable);
    }
    ttscommit;
}

public void executeQuery()
{
    QueryBuildDataSource qbLeadTableDataSource;
    QueryBuildDataSource qbDirNameDataSource;

    qbLeadDataSource = this.query().dataSourceTable(tableNum(smmLeadTable));

    if (this.queryRun())
    {
        qbLeadTableDataSource = this.queryRun().query().dataSourceTable(tableNum(smmLeadTable));
        qbDirNameDataSource = this.queryRun().query().dataSourceTable(tableNum(DirPartyTable));

        // If user wants to sort on Leads , then sorting on DirPartyTable should be cleared
        if (sortedOnDirName && qbDirNameDataSource.sortFieldCount()
            && qbLeadTableDataSource.sortFieldCount())
        {
            qbDirNameDataSource.sortClear();
            sortedOnDirName = false;
        }
        // If user wants to sort on DirPartyTable , then sorting on Leads should be cleared
        else if (!sortedOnDirName && qbDirNameDataSource.sortFieldCount()
                && (qbLeadTableDataSource.sortFieldCount() ||
                    qbLeadTableDataSource.sortIndexCount()))
        {
            qbLeadTableDataSource.sortClear();
            sortedOnDirName = true;
        }
    }
    super();

    element.setProcessControls();
    element.setAccountControls();
}

