    {
        //
        // Filter by the the currently active lead
        //
        responsibilityRefTableIdRange.value(queryValue(tableNum(smmLeadTable)));
        responsibilityRefRecIdRange.value(queryValue(smmLeadTable.RecId));

        super();
    }
}

public void init()
{
    super();

    //
    // Cache the display methods
    //
    this.cacheAddMethod(tableMethodStr(smmResponsibilitiesEmplTable, emplName));

    //
    // The query on any related datasource should only depend on the currently
    // active lead. So, clear any automaticaly created dynalinks.
    //
    this.query().dataSourceNo(1).clearDynalinks();

    //
    // Create a ranges for filtering Lead.
    //
    responsibilityRefTableIdRange = this.query().dataSourceNo(1).addRange(fieldNum(smmResponsibilitiesEmplTable, RefTableId));
    responsibilityRefRecIdRange = this.query().dataSourceNo(1).addRange(fieldNum(smmResponsibilitiesEmplTable, RefRecId));
}

