public boolean validateWrite()
{
    boolean             ret;
    smmParametersTable  smmParametersTable = smmParametersTable::find();

    ret = super();

    if (ret && (dirPartyTable.RecId && !DirPartyTableHelper::isCustomerOrRelation(dirPartyTable.RecId)) || !dirPartyTable.RecId)
    {
        if (!(smmParametersTable.LanguageId && smmParametersTable.CurrencyCode && smmParametersTable.BusRelTypeId))
        {
            ret = checkFailed("@SYS105615");
        }
    }
    return ret;
}

public void write()
{
    DirPartyBaseType partyType = DirAppParameters::find().BusRelDefaultPartyType;

    super();

    if (partyType == DirPartyBaseType::Person && dirCheckFullName)
    {
        // Save CheckFullName info, then set to null, so edits on other lead records don't try to "saveNameInfo"
        dirCheckFullName.saveNameInfo(dirPartyTable.RecId);
        dirCheckFullName = null;
    }
}

