public void lookup(FormControl _formControl, str _filterStr)
{
    DirPartyTable selectedRecord;

    // Allow name lookup if lead is not yet created
    if (!smmLeadTable.RecId )
    {
        partyForm.parmForceDuplicateCheck(false);
        selectedRecord = DirUtilityEx::namelookup(_formControl, smmLeadTable, DirPartyType::None);

        if (selectedRecord.RecId)
        {
            dirPartyTable.data(selectedRecord);
        }
    }
}

public void modified()
{
    FormStringControl   control;
    DirPartyRecId       oldParty;
    DirPartyTable       selectedRecord;

    oldParty = smmLeadTable.Party;
    partyForm.parmForceDuplicateCheck(true);

    super();

    // If existing party
    if (!smmLeadTable.RecId && smmLeadTable.Party && dirPartyTable.RecId == smmLeadTable.Party)
    {
        dirPartyTable_ds.object(fieldNum(DirPartyTable, Name)).allowEdit(false);
        dirCheckFullName = null;
    }

    // If party is not yet identified and on basis of name there is not a single match for party
    if (!smmLeadTable.Party && DirPartyTable::nameLikeCount(dirPartyTable.Name, dirPartyTable.type()) >= 1 && !dirPartyTable.Name)
    {
        control = element.selectedControl();

        if (!control && (general.isActivePage()  && element.selectControl(groupLeads_PartyName)))
        {
            control = element.selectedControl();
        }

        selectedRecord = DirUtilityEx::namelookup(control, smmLeadTable, dirPartyTable.type());

        if (selectedRecord.RecId)
        {
            dirPartyTable.data(selectedRecord);
        }
    }

    // If new party
    if (!dirPartyTable && DirAppParameters::find().BusRelDefaultPartyType == DirPartyBaseType::Person)
    {
        if (!dirCheckFullName)
        {
            dirCheckFullName = new DirCheckFullName();
        }

        // Check full name
        dirCheckFullName.showFullNameCheck(dirPartyTable);
    }

    if (!smmLeadTable.RecId && (smmLeadTable.Party || oldParty))
    {
        DirParty::showHideMessageBar(element,smmLeadTable);
    }

    dirPartyTable_ds.object(fieldNum(DirPartyTable, Name)).allowEdit(!dirPartyTable.RecId);
}

