public void jumpRef()
{
    if (sourceType.text())
    {
        if (smmLeadTable.SourceRefTableId != tableNum(smmSourceTypeOptions))
        {
            smmUtility::peformJumpRef(smmLeadTable.SourceRefTableId, smmLeadTable.SourceRefRecId);
        }
        else
        {
            smmUtility::peformJumpRef(tableNum(smmSourceTypeTable), smmSourceTypeTable::find(smmLeadTable.SourceTypeId).RecId);
        }
    }
}

public void lookup()
{
    SysTableLookup       sysTableLookup = SysTableLookup::newParameters(tableNum(smmSourceTypeOptions), this);
    Query                query;
    QueryBuildDataSource queryBuildDataSource;
    QueryBuildRange      queryBuildRange;

    if (sourceType.text())
    {
        if (smmLeadTable.SourceRefTableId == tableNum(HcmWorker))
        {
            // Lookup for Type Option HCMWorker
            query = new Query();
            sysTableLookup = SysTableLookup::newParameters(tableNum(HcmWorker), this);
            queryBuildDataSource = query.addDataSource(tableNum(HcmWorker));

            sysTableLookup.addLookupfield(fieldNum(HcmWorker, PersonnelNumber),true);
            sysTableLookup.addLookupfield(fieldNum(HcmWorker, Person));

            sysTableLookup.parmQuery(query);
            sysTableLookup.performFormLookup();
        }
        else
        {
            if (smmLeadTable.SourceRefTableId != tableNum(smmSourceTypeOptions))
            {
                this.performTypeLookup((new SysDictField(smmLeadTable.SourceRefTableId,
                                        smmSourceType::tableId2FieldId(smmLeadTable.SourceRefTableId)).typeId()));
            }
            else
            {
                // Lookup for Type Options or User-Defined Types

                sysTableLookup.addLookupfield(fieldNum(smmSourceTypeOptions, Name),true);
                sysTableLookup.addLookupfield(fieldNum(smmSourceTypeOptions, Description));

                query = new Query();
                queryBuildDataSource = query.addDataSource(tableNum(smmSourceTypeOptions));
                queryBuildRange = queryBuildDataSource.addRange(fieldNum(smmSourceTypeOptions,TypeId));
                queryBuildRange.value(queryValue(smmLeadTable.SourceTypeId));

                sysTableLookup.parmQuery(query);
                sysTableLookup.performFormLookup();
            }
        }
    }
}

