// This is the generic CreateEntity method which is called by
// Create customer and Create opportunity functions
//
void createEntity(smmEntityToCreate _entityCreate)
{
    NoYes               conversionSuccessful;
    MenuFunction        menuFunction;
    smmBusRelTable      smmBusRelTable;
    Args                args = new Args();

    // from Create Customer function button
    if (_entityCreate == smmEntityToCreate::Customer)
    {
        // Convert to customer
        smmBusRelTable = smmBusRelTable::findByParty(smmOpportunityTable.Party);
        if (smmBusRelTable.canConvertToCustomer())
        {
            conversionSuccessful = smmBusRelTable::convert2Customer(smmBusRelTable);
        }
        if (conversionSuccessful)
        {
            // Open the Customer table form if configured to do so
            if (smmParameters::getBusRelOpenCustForm())
            {
                menuFunction = new MenuFunction(menuitemDisplayStr(CustTable), MenuItemType::Display);
                menuFunction.openMode(OpenMode::Edit);

                // Check access rights
                if (!menuFunction || !menuFunction.checkAccessRights())
                {
                    // Access rights are not sufficient to use this function.
                    throw error("@SYS81158");
                }

                args.record(smmBusRelTable);
                menuFunction.run(args);
            }
        }
    }
    else if (_entityCreate == smmEntityToCreate::Lead)
    {
        smmUtility::openMenuItemForm(menuitemDisplayStr(smmLeadTable),smmOpportunityTable,element,true);
    }

    // Refresh form
    smmOpportunityTable_ds.reread();
    smmOpportunityTable_ds.active();
}

public void createQuotation(QuotationType _quotationType)
{
    SalesQuotationTableForm     salesQuotationTableForm;

    salesQuotationTableForm = SalesQuotationTableForm::construct(_quotationType,smmOpportunityTable);

    if (salesQuotationTableForm.create())
    {
        element.parmQuotationId(salesQuotationTableForm.salesQuotationTable().QuotationId);
        element.openQuotation(salesQuotationTableForm.salesQuotationTable().QuotationType);
    }
}

public void createRecord(str _formDataSourceName, boolean _append = false)
{
    Map typesToCreate;

    if (_formDataSourceName == smmOpportunityTable_ds.name() ||
       _formDataSourceName == dirPartyTable_ds.name())
    {
        typesToCreate = new Map(Types::String, Types::String);
        typesToCreate.insert(smmOpportunityTable_ds.name(), tableStr(DirPartyTable));

        // New party
        if (this.isPartyPerson(callerRecord))
        {
            typesToCreate.insert(dirPartyTable_ds.name(), tableStr(DirPerson));
        }
        else
        {
            typesToCreate.insert(dirPartyTable_ds.name(), tableStr(DirOrganization));
        }

        smmOpportunityTable_ds.createTypes(typesToCreate);
        dirOrgPersonRelations_ds.initValue();
    }
    else
    {
        super(_formDataSourceName, _append);
    }
}

/// <summary>
/// Allows to maintain logic for creating Opportunity to party of type Person.
/// </summary>
/// <returns> 'true' if party's type is Person and 'false' if type is Organization.
private boolean isPartyPerson(Common _callerRecord)
{
    if (DirAppParameters::find().BusRelDefaultPartyType == DirPartyBaseType::Person)
    {
        return true;
    }

    boolean isPartyPerson = false;

    if (_callerRecord && _callerRecord is smmBusRelTable)
    {
        smmBusRelTable smmbusreltable = _callerRecord as smmBusRelTable;
        isPartyPerson = smmbusreltable.partyType() == DirPartyType::Person;
    }
    else if (_callerRecord && _callerRecord is CustTable && SmmOpportunityCreateForCustomerPersonFlight::instance().isEnabled())
    {
        CustTable custTable = callerRecord as CustTable;
        isPartyPerson = custTable.partyType() == DirPartyType::Person;
    }

    return isPartyPerson;
}

