public void init()
{
    if (SysFormRun::isCalledFromJumpRef(this.args(), fieldNum(smmOpportunityTable, OpportunityId)) ||
        element.args().dataset() == tableNum(smmOpportunityTable) ||
        element.args().dataset() == tableNum(smmOpportunityRelTable))
