    {
        callerRecord = element.args().record();
    }

    conQuoteType = SalesQuotationTableType::setAllowedTypes();

    hcmWorkerLookupCurrentCompany = HcmWorkerLookup::newCustomOptions(true, true, true, false, true, true, true, true, true);
}

NumberSeqFormHandler numberSeqFormHandler(TableId _tableId)
{
    // Activate number sequence handler for the form
    if (!numberSeqFormHandler)
    {
        if (_tableId == tableNum(smmOpportunityTable))
        {
            numberSeqFormHandler = NumberSeqFormHandler::newForm(smmParameters::numRefOpportunityId().NumberSequenceId,
                                                                 element,
                                                                 smmOpportunityTable_ds,
                                                                 fieldNum(smmOpportunityTable, OpportunityId));
        }
        if (_tableId == tableNum(smmActivities))
        {
            numberSeqFormHandler = NumberSeqFormHandler::newForm(smmParameters::numRefActivityNumber().NumberSequenceId,
                                                                 element,
                                                                 smmActivities_ds,
                                                                 fieldNum(smmActivities, ActivityNumber));
        }
    }
    return numberSeqFormHandler;
}

