void setAccountControls()
{
    boolean isProspect;
    boolean isCustomer;
    DirOrgPersonRelations   dirOrgPersonRelationsforOppor;

    isProspect=smmOpportunityTable.RecId != 0 && DirPartyTableHelper::isRelation(smmOpportunityTable.Party);
    isCustomer=smmOpportunityTable.RecId != 0 && DirPartyTableHelper::isCustomer(smmOpportunityTable.Party);

    select firstonly EntityRefRecId, EntityRefTableId from dirOrgPersonRelationsforOppor
        where dirOrgPersonRelationsforOppor.EntityRefRecId == smmOpportunityTable.RecId &&
              dirOrgPersonRelationsforOppor.EntityRefTableId == smmOpportunityTable.TableId;

    prospectButton.enabled(isProspect);
    customerButton.enabled(isCustomer);
}

void setProcessControls()
{
    salesProcessButton.enabled(smmOpportunityTable.ProcessName != '');
    stageButton.enabled(smmOpportunityTable.ProcessName != '');

    if (smmOpportunityTable.ProcessName)
    {
        smmProcessInstance = smmProcessInstance::construct(smmOpportunityTable);
    }
}

public boolean setProcessInstance(HierarchyName _oldProcess, HierarchyName _newProcess)
{
    DialogButton    answer = DialogButton::Yes;

    if (_oldProcess && _oldProcess !=_newProcess)
    {
        // Do you want to remove all open activities?
        answer = Box::yesNoCancel("@SYS109839", DialogButton::Cancel);

        if (answer == DialogButton::Cancel)
        {
            return checkFailed("@SYS25904"); //Update has been canceled.
        }
    }

    smmProcessInstance::write(smmOpportunityTable, _oldProcess, _newProcess, false, answer == DialogButton::Yes);

    return true;
}

