public int active()
{
    int     ret;
    boolean isCustomer;

    ret = super();

    // Disable create sales quotation if record is not yet saved or no Party id
    // and status is not won, cancelled or lost
    if (!smmOpportunityTable.RecId ||
        smmOpportunityTable.Status == smmOpportunityStatus::Won ||
        smmOpportunityTable.Status == smmOpportunityStatus::Cancelled ||
        smmOpportunityTable.Status == smmOpportunityStatus::Lost ||
        conQuoteType == conNull())
    {
        salesQuotationTable_ds.allowEdit(false);
        salesQuotationTable_ds.allowCreate(false);
        salesQuotationTable_ds.allowDelete(false);
    }
    else
    {
        salesQuotationTable_ds.allowEdit(true);
        salesQuotationTable_ds.allowCreate(true);
        salesQuotationTable_ds.allowDelete(true);
    }

    isCustomer = DirPartyTableHelper::isCustomer(smmOpportunityTable.Party);

    if (!smmOpportunityTable.RecId || !smmOpportunityTable.Party || isCustomer)
    {
        createCustomer.enabled(false);
    }
    else
    {
        createCustomer.enabled(true);
    }

    customerButton.enabled(smmOpportunityTable.RecId && DirPartyTableHelper::isRelation(smmOpportunityTable.Party) && isCustomer);

    smmOpportunityTable_ds.allowEdit(smmOpportunityTable.Status != smmOpportunityStatus::Won);

    // Sales process
    element.setProcessControls();
    // Sales accounts
    element.setAccountControls();

    // Disable Name if existing record
    dirPartyTable_ds.object(fieldNum(DirPartyTable, Name)).allowEdit(!smmOpportunityTable.RecId);

    if (!smmOpportunityTable.RecId || !smmOpportunityTable.Party ||
        smmOpportunityTable.Status == smmOpportunityStatus::Won ||
        smmOpportunityTable.Status == smmOpportunityStatus::Cancelled ||
        smmOpportunityTable.Status == smmOpportunityStatus::Lost)
    {
        createQuotation.enabled(false);
        CreateProjectQuotation.enabled(false);
    }
    else
    {
        createQuotation.enabled(true);
        CreateProjectQuotation.enabled(true);
    }

    Quotations.enabled(SalesQuotationTable::existsSalesQuotationForOpportunity(smmOpportunityTable.OpportunityId));
    ProjectQuotations.enabled(SalesQuotationTable::existsProjectQuotationForOpportunity(smmOpportunityTable.OpportunityId));

    return ret;
}

public void executeQuery()
{
    smmOpportunityTable smmOpportunityTableLocal = null;
    QueryBuildDataSource qbOpportunityTableDataSource;
    QueryBuildDataSource qbDirNameDataSource;

    if (this.queryRun())
    {
        qbOpportunityTableDataSource = this.queryRun().query().dataSourceTable(tableNum(smmOpportunityTable));
        qbDirNameDataSource = this.queryRun().query().dataSourceTable(tableNum(DirPartyTable));

        // If user wants to sort on Opportunities , then sorting on DirPartyTable should be cleared
        if (sortedOnDirName && qbDirNameDataSource.sortFieldCount()
            && qbOpportunityTableDataSource.sortFieldCount())
        {
            qbDirNameDataSource.sortClear();
            sortedOnDirName = false;
        }
        // If user wants to sort on DirPartyTable , then sorting on Opportunities should be cleared
        else if (!sortedOnDirName && qbDirNameDataSource.sortFieldCount()
                && (qbOpportunityTableDataSource.sortFieldCount() ||
                    qbOpportunityTableDataSource.sortIndexCount()))
        {
            qbOpportunityTableDataSource.sortClear();
            sortedOnDirName = true;
        }
    }

    super();

    element.setProcessControls();
    element.setAccountControls();
}

public void init()
