}

public void write()
{
    NoYes           newRecord;
    HierarchyName   oldProcess;
    boolean         ret;
    ttsbegin;

    element.numberSeqFormHandler(tableNum(smmOpportunityTable)).formMethodDataSourceWrite();

    if (!dirPartyTable.RecId)
    {
        dirPartyTable_ds.write();
    }

    smmOpportunityTable.Party = dirPartyTable.RecId;

    newRecord = smmOpportunityTable.RecId ? NoYes::No : NoYes::Yes;

    oldProcess = smmOpportunityTable.orig().ProcessName;

    super();

    // Sales process
    ret = element.setProcessInstance(oldProcess, smmOpportunityTable.ProcessName) ;

    if (!ret)
    {
        ttsabort;
        smmOpportunityTable_ds.reread();
        return;
    }

    // If called from other form and creating a new record
    if (callerRecord && newRecord)
    {
        // Copy associations for the caller record if the caller is not smmOpportunitiesListPage
        if (element.args().caller() && element.args().caller().name() != formStr(smmOpportunityTable))
        {
            smmCreateEntity::createLinkCommon(callerRecord, smmOpportunityTable);
        }
    }
    if (newRecord)
    {
        DirParty::showHideMessageBar(element,smmOpportunityTable);
    }
    ttscommit;
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        ret = element.numberSeqFormHandler(tableNum(smmOpportunityTable)).formMethodDataSourceValidateWrite(ret) && ret;
        ret = ret && partyForm.validateWrite();
    }

    return ret;
}

public void linkActive()
{
    smmResponsibilityGroup  responsibilityGroup;
    Hierarchy               hierarchy;
    QueryBuildDataSource    queryBuildDataSource;
    element.numberSeqFormHandler(tableNum(smmOpportunityTable)).formMethodDataSourceLinkActive();

    // Is form called with a record
    if (element.args() && element.args().record())
    {
        callerRecord = element.args().record();
        switch (callerRecord.TableId)
        {
            // Is the form called from the contact person form
            case tableNum(ContactPerson) :
            queryBuildDataSource = this.query().dataSourceTable(tableNum(DirOrgPersonRelations));
            if (!queryBuildDataSource)
            {
                queryBuildDataSource = this.query().dataSourceTable(tableNum(smmOpportunityTable)).addDataSource(tableNum(DirOrgPersonRelations));
                queryBuildDataSource.relations(true);
                queryBuildDataSource.joinMode(JoinMode::ExistsJoin);
                queryBuildDataSource.addDynalink(fieldNum(DirOrgPersonRelations,ContactPersonId), element.args().record(), fieldNum(ContactPerson,ContactPersonId));
            }
            break;

            // Is form called from Leads form
            case tableNum(smmLeadTable) :
                queryBuildDataSource = this.query().dataSourceTable(tableNum(smmLeadRelTable));
                if (!queryBuildDataSource)
                {
                    queryBuildDataSource = this.query().dataSourceTable(tableNum(smmOpportunityTable)).addDataSource(tableNum(smmLeadRelTable));
                    queryBuildDataSource.joinMode(JoinMode::ExistsJoin);
                    queryBuildDataSource.relations(true);
                    queryBuildDataSource.addDynalink(fieldNum(smmLeadRelTable, LeadId), callerRecord, fieldNum(smmLeadTable, LeadId));
                }
                break;

            // Is the form called from the activities form
            case tableNum(smmActivities) :
                smmActivityParentLink::updateQuery(this.query().dataSourceTable(tableNum(smmOpportunityTable)), element.args().record());
                break;

            // Is the form called from the responsibilities form
            case tableNum(smmResponsibilityGroup) :
                // Get the current selected responsibilities record
                responsibilityGroup  = element.args().record();

                // Update query
                smmResponsibility::updateQuery(this.query(),this.table(),responsibilityGroup.ResponsibilityId);
                break;

            // Is the form called from the process template form
            case tableNum(Hierarchy) :
                hierarchy = element.args().record();
                selectedProcess = hierarchy.Name;
                break;

            case tableNum(smmQuotationCompetitorGroup) :
                queryBuildDataSource = this.query().dataSourceTable(tableNum(smmQuotationCompetitors));
                if (!queryBuildDataSource)
                {
                    queryBuildDataSource = this.query().dataSourceTable(tableNum(smmOpportunityTable)).addDataSource(tableNum(smmQuotationCompetitors));
                    queryBuildDataSource.joinMode(JoinMode::ExistsJoin);
                    queryBuildDataSource.relations(true);
                    queryBuildDataSource.addDynalink(fieldNum(smmQuotationCompetitors, Party), callerRecord, fieldNum(smmQuotationCompetitorGroup, Party));
                }
                break;

            case tableNum(smmCampaignTable) :
                queryBuildDataSource = this.query().dataSourceTable(tableNum(smmCampaignTable));
                if (!queryBuildDataSource)
                {
                    queryBuildDataSource = this.query().dataSourceTable(tableNum(smmOpportunityTable)).addDataSource(tableNum(smmOpportunityRelTable));
                    queryBuildDataSource.relations(true);
                    queryBuildDataSource.joinMode(JoinMode::ExistsJoin);
                    queryBuildDataSource.addDynalink(fieldNum(smmOpportunityRelTable, RefRecId), element.args().record(), fieldNum(smmCampaignTable, RecId));
                    queryBuildDataSource.addRange(fieldNum(smmOpportunityRelTable, RefTableId)).value(int2str(tableNum(smmCampaignTable)));
                }
                break;

            case tableNum(smmBusRelTable) :
                queryBuildDataSource = this.query().dataSourceTable(tableNum(smmBusRelTable));
                if (!queryBuildDataSource)
                {
                    queryBuildDataSource = this.query().dataSourceTable(tableNum(smmOpportunityTable)).addDataSource(tableNum(smmBusRelTable));
                    queryBuildDataSource.joinMode(JoinMode::ExistsJoin);
                    queryBuildDataSource.relations(true);
                    queryBuildDataSource.addDynalink(fieldNum(smmBusRelTable, Party), callerRecord, fieldNum(smmBusRelTable, Party));
                }
                break;

            case tableNum(CustTable) :
                queryBuildDataSource = this.query().dataSourceTable(tableNum(CustTable));
                if (!queryBuildDataSource)
                {
                    queryBuildDataSource = this.query().dataSourceTable(tableNum(smmOpportunityTable)).addDataSource(tableNum(CustTable));
                    queryBuildDataSource.joinMode(JoinMode::ExistsJoin);
                    queryBuildDataSource.relations(true);
                    queryBuildDataSource.addDynalink(fieldNum(CustTable, Party), callerRecord, fieldNum(CustTable, Party));
                }
                break;
        }
    }

    super();
}

public void delete()
{
    boolean ret;

    if (!smmOpportunityTable.RecId  || (smmEntityDelete && smmOpportunityTable_ds.anyMarked()))
    {
        ret = true;
    }
    else
    {
        smmEntityDelete = SmmEntityDelete::construct(smmOpportunityTable,false);
        ret = smmEntityDelete && smmEntityDelete.prompt();
    }

    if (ret)
    {
        ttsbegin;
        if (smmEntityDelete)
        {
            smmEntityDelete.parmCommonTable(smmOpportunityTable);
            smmEntityDelete.runOperation();
        }
        element.numberSeqFormHandler(tableNum(smmOpportunityTable)).formMethodDataSourceDelete();

        super();

        ttscommit;
        DirParty::showHideMessageBar(element,smmOpportunityTable);
    }
}

