public void initValue()
{
    super();

    smmOpportunityTable.Status = smmOpportunityStatus::Active;

    // If opportunity form is called from other form
    if (callerRecord)
    {
        // Init opportunity from caller record
        if (smmCreateEntity::initFromCommon(callerRecord, smmOpportunityTable))
        {
            if (smmOpportunityTable.Party != dirPartyTable.RecId)
            {
                dirPartyTable.data(DirPartyTable::findRec(smmOpportunityTable.Party));
            }
            DirParty::showHideMessageBar(element,smmOpportunityTable);
        }
    }
}

