public void init()
{
    super();

    //
    // The query on any related datasource should only depend on the currently
    // active opportunity. So, clear any automaticaly created dynalinks.
    //
    this.query().dataSourceNo(1).clearDynalinks();

    //
    // Create a range for filtering on opportunity.
    //
    swotRefTableIdRange = this.query().dataSourceNo(1).addRange(fieldNum(smmSwotTable,RefTableId));
    swotRefRecIdRange = this.query().dataSourceNo(1).addRange(fieldNum(smmSwotTable,RefRecId));

    this.query().dataSourceNo(1).clearDynalinks();
}

