public void init()
{
    super();

    //
    // Cache the display methods
    //
    this.cacheAddMethod(tableMethodStr(smmResponsibilitiesEmplTable, emplName));

    //
    // The query on any related datasource should only depend on the currently
    // active opportunity. So, clear any automaticaly created dynalinks.
    //
    this.query().dataSourceNo(1).clearDynalinks();

    //
    // Create a ranges for filtering on Business Relation.
    //
    responsibilityRefTableIdRange = this.query().dataSourceNo(1).addRange(fieldNum(smmResponsibilitiesEmplTable, RefTableId));
    responsibilityRefRecIdRange = this.query().dataSourceNo(1).addRange(fieldNum(smmResponsibilitiesEmplTable, RefRecId));
}

public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(smmResponsibilityGroup), _formControl);
    QueryBuildDataSource            queryBuildDataSource;
    Query                           query;
    QueryBuildRange                 queryBuildRange;

    sysTableLookup.addLookupfield(fieldNum(smmResponsibilityGroup, ResponsibilityId),true);
    sysTableLookup.addLookupfield(fieldNum(smmResponsibilityGroup, Description));

    query = new Query();

    queryBuildDataSource = query.addDataSource(tableNum(smmResponsibilityGroup));

    queryBuildRange = queryBuildDataSource.addRange(smmResponsibility::hierarchyType2FieldIdForFlag(HierarchyType::Opportunity));

    queryBuildRange.value(queryValue(NoYes::Yes));

    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();
}

