public void init()
{
    QueryBuildDataSource    qbdSmmActivityParentLinkTable;
    QueryBuildDataSource    qbdHierarchyTreeTable;

    super();

    //
    // Cache the display methods
    //
    this.cacheAddMethod(tableMethodStr(smmActivities, showFollowUpIcon));
    this.cacheAddMethod(tableMethodStr(smmActivities, showDocHanIcon));

    //
    // The query on any related datasource should only depend on the currently
    // active opportunity. So, clear any automaticaly created dynalinks.
    //
    this.query().dataSourceNo(1).clearDynalinks();

    qbdSmmActivityParentLinkTable = this.query().dataSourceNo(1).addDataSource(tableNum(smmActivityParentLinkTable));
    qbdSmmActivityParentLinkTable.relations(true);
    qbdSmmActivityParentLinkTable.addRange(fieldNum(smmActivityParentLinkTable, ParentType)).value(queryValue(smmActivityParentType::Opportunity));
    qbdSmmActivityParentLinkTable.addDynalink(fieldNum(smmActivityParentLinkTable, RefRecId), smmOpportunityTable, fieldNum(smmOpportunityTable,RecId));

    qbdHierarchyTreeTable =  this.query().dataSourceName(tableStr(smmActivities)).addDataSource(tableNum(HierarchyTreeTable));
    qbdHierarchyTreeTable.joinMode(JoinMode::NoExistsJoin);
    qbdHierarchyTreeTable.relations(true);
    qbdHierarchyTreeTable.addRange(fieldNum(HierarchyTreeTable, ElementNodeType)).value(queryValue(ElementNodeType::Node));
}

