public void delete()
{
    if (salesQuotationTable.RecId != 0 &&
        Box::yesNo(strFmt("@SYS104180",salesQuotationTable.QuotationId),DialogButton::No,"@SYS28002") == DialogButton::Yes)
    {
        //
        // Remove link to sales quotation
        //
        salesQuotationTable.OpportunityId = '';
        salesQuotationTable.update();

        salesQuotationTable_ds.reread();
        salesQuotationTable_ds.executeQuery();
    }
}

public void init()
{
    super();

    // Show only non-template quotes
    this.query().dataSourceTable(tableNum(SalesQuotationTable)).addRange(fieldNum(SalesQuotationTable, TemplateName)).value(SysQuery::valueEmptyString());
    this.query().dataSourceTable(tableNum(SalesQuotationTable)).addRange(fieldNum(SalesQuotationTable, TemplateGroupId)).value(SysQuery::valueEmptyString());

    salesOppIdRange = this.query().dataSourceTable(tableNum(SalesQuotationTable)).addRange(fieldNum(SalesQuotationTable, OpportunityId));
    quotationTypeRange = this.query().dataSourceTable(tableNum(SalesQuotationTable)).addRange(fieldNum(SalesQuotationTable, QuotationType));
}

public void executeQuery()
{
    if (smmOpportunityTable.RecId && conQuoteType != conNull())
    {
        quotationTypeRange.value(con2Str(conQuoteType));
    }
    else
    {
        salesOppIdRange.value(SysQuery::valueNotEmptyString());
    }

    super();
}

