        }
    }
}

public void deleteMarked()
{
    Common              common;
    SalesQuotationTable salesQuotationTableRecord;

    common = salesQuotationTable_ds.getFirst(1);

    if (common.RecId != 0 &&
       (Box::yesNo("@SYS100114",DialogButton::No,"@SYS28002") == DialogButton::Yes))
    {
        while (common.RecId != 0)
        {
            //
            // Remove link to sales quotation
            //
            salesQuotationTableRecord = SalesQuotationTable::findRec(common.RecId, true);
            salesQuotationTableRecord.OpportunityId = '';
            salesQuotationTableRecord.update();

            common = salesQuotationTable_ds.getNext();
        }
        salesQuotationTable_ds.reread();
        salesQuotationTable_ds.executeQuery();
    }
}

