public void lookup(FormControl _formControl, str _filterStr)
{
    DirPartyTable selectedRecord;

    // Allow name lookup if opportunity is not yet created
    if (!smmOpportunityTable.RecId )
    {
        partyForm.parmForceDuplicateCheck(false);
        selectedRecord = DirUtilityEx::namelookup(_formControl, smmOpportunityTable, DirPartyType::None);

        if (selectedRecord.RecId)
        {
            dirPartyTable.data(selectedRecord);
        }
    }
}

public void modified()
{
    FormStringControl   control;
    DirPartyRecId       oldParty;
    DirPartyTable       selectedRecord;

    oldParty = smmOpportunityTable.Party;
    partyForm.parmForceDuplicateCheck(true);

    super();

    if (!smmOpportunityTable.RecId && smmOpportunityTable.Party && dirPartyTable.RecId == smmOpportunityTable.Party)
    {
        dirPartyTable_ds.object(fieldNum(DirPartyTable, Name)).allowEdit(false);
        dirCheckFullName = null;
    }

    // If party is not yet identified and on basis of name there is not a single match for party
    if (!dirPartyTable.RecId && DirPartyTable::nameLikeCount(dirPartyTable.Name, dirPartyTable.type()) >= 1 && !dirPartyTable.Name)
    {
        control = element.selectedControl();
        if (!control && (overviewTab.isActivePage() && element.selectControl(grid_DirEntryName)) ||
                        (genaralTab.isActivePage()  && element.selectControl(partyGroup_Name))
           )
        {
            control = element.selectedControl();
        }

        selectedRecord = DirUtilityEx::namelookup(control, smmOpportunityTable, dirPartyTable.type());

        if (selectedRecord.RecId)
        {
            dirPartyTable.data(selectedRecord);
        }
    }

    // If new party
    if (!dirPartyTable.RecId && DirAppParameters::find().BusRelDefaultPartyType == DirPartyBaseType::Person)
    {
        if (!dirCheckFullName)
        {
            dirCheckFullName = new DirCheckFullName();
        }

        // Check full name
        dirCheckFullName.showFullNameCheck(dirPartyTable);
    }

    if (!smmOpportunityTable.RecId && (smmOpportunityTable.Party || oldParty))
    {
        DirParty::showHideMessageBar(element,smmOpportunityTable);
    }

    dirPartyTable_ds.object(fieldNum(DirPartyTable, Name)).allowEdit(!dirPartyTable.RecId);
}

