public class smmQuotationCompetitorGroup extends FormRun implements ILogisticsPostalAddressGrid, ILogisticsContactInfoGrid
{
    QueryBuildRange             swotRefTableIdQBR;
    QueryBuildRange             swotRefRecIdQBR;
    QueryBuildRange             addrTableIdRange, addrRecIdRange;
    QueryBuildRange             partyIdRange, refTableIdRange;
    boolean                     sortedOnDirPartyTable;

    container                   mainDatasources;

    DirPartyFormHandler_Entity  partyForm;

    SysFormEnumComboBox         typeComboBox;
    DirPartyType                lastPartyType;
    SysLookupMultiSelectCtrl    addressBooksCtrl;
    container                   defaultRole;
    boolean                     setPartyType;

}

public void createRecord(str _formDataSourceName, boolean _append = false)
{
    if (conFind(mainDatasources, this.dataSource(_formDataSourceName).id()))
    {
        partyForm.initValue();
        partyForm.create();
    }
    else
    {
        super(_formDataSourceName, _append);
    }
}

public void init()
{
    boolean     isSmmquotationCompetitorGroupTable = false;

    if (element.args().dataset() == tableNum(smmQuotationCompetitorGroup))
    {
        isSmmquotationCompetitorGroupTable = true;
    }

    typeComboBox = DirPartyFormHandler::updateTypeComboBox(element, formControlStr(smmQuotationCompetitorGroup, Type));

    super();

    dirPartyTable_DirOrganizationBase_ds.addFieldToSelectionList(fieldNum(DirOrganizationBase, DunsNumberRecId));

    mainDatasources = [smmQuotationCompetitorGroup_ds.id(), dirPartyTable_ds.id(), dirPartyTable_DirOrganization_ds.id(), dirPartyTable_DirPerson_ds.id(), dirPersonName_ds.id(), dirPartyTable_DirOrganizationBase_ds.id()];

    addressBooksCtrl = SysLookupMultiSelectCtrl::construct(element, addressBooks, queryStr(DirAddressBookLookup));

    partyForm = new DirPartyFormHandler_Entity(element);
    partyForm.setDatasourceCaller(smmQuotationCompetitorGroup_ds);
    partyForm.setDatasourceParty(dirPartyTable_ds);
    partyForm.setDatasourcePartyName(dirPersonName_ds);
    partyForm.ctrlHideShowButton(hideShowButton);
    partyForm.ctrlHideShowComboBox(hideShowComboBox);
    partyForm.ctrlTabPageGeneral(generalTab);
    partyForm.parmAddressBookCtrl(addressBooksCtrl);
    partyForm.setNameControls(org_Name, name_FirstName, name_MiddleName, name_LastName);

    setPartyType = false;
}

public DirPartyFormHandler_Entity parmPartyForm()
{
    return partyForm;
}

public void run()
{
    super();

    //
    // If this form is called from Home/Global Address Book then we need to retrieve
    // the full buffer of DirPartyTable so that all the dependent tables will have
    // data along with parent table otherwise the fields related to DirPerson will be
    // disabled and will show text as Unretrieved
    //
    if (element.args().caller())
    {
        if (element.args().caller().name() == formStr(DirPartyTable))
        {
            dirPartyTable.data(DirPartyTable::findRec(element.args().record().RecId));
            dirPartyTable_ds.rereadReferenceDataSources();

            if (dirPartyTable.type() == DirPartyType::Person)
            {
                //
                // Fetch the existing name record and populate the table buffer with it so that
                // it does not attempt to create a new date effective version record for the table
                //
                dirPersonName.data(DirPersonName::find(DirPartyTable.RecId));
            }

            smmQuotationCompetitorGroup_ds.forceWrite(true);
        }
    }

    setPartyType = true;
    element.updateDesign();
}

public boolean selectControl(FormControl _control)
{
    boolean ret;
    if (dirPartyTable is DirPerson)
    {
        ret = partyForm.selectControl(_control, dirPersonName);

        if (!ret)
        {
            return false;
        }
    }

    ret = super(_control);

    return ret;
}

public void updateControls()
{
    boolean         newRecord = (smmQuotationCompetitorGroup.RecId == 0);
    boolean         newPartyRecord = (dirPartyTable.RecId == 0);

    if (!newPartyRecord)
    {
        addressBooksCtrl.set(dirPartyTable.addressBooks());
    }

    typeComboBox.select(dirPartyTable.baseType());

    type.allowEdit(newRecord);
    lastPartyType = any2Enum(typeComboBox.selection());
}

public void updateDesign()
{
    DirPartyType partyType = dirPartyTable.type();
    boolean      isInternalOrg = (partyType != DirPartyType::Organization && partyType != DirPartyType::Person);
    boolean      isOrganization = (partyType == DirPartyType::Organization);
    boolean      isPerson = (partyType == DirPartyType::Person);

    if (generalTab.isActivePage())
    {
        // Organization
        orgGroup1.visible(!isPerson);
        orgGroup3.visible(!isPerson);
        orgGroup2.visible(isOrganization);
        orgPhoneticGroup.visible(isOrganization);

        // Person
        personGroup1.visible(isPerson);
        personGroup2.visible(isPerson);
        personGroup3.visible(isPerson);

        hideShowGroup.visible(!isInternalOrg);
    }

    if (!isInternalOrg && (setPartyType || generalTab.isActivePage()))
    {
        if (setPartyType)
        {
            partyForm.parmPartyType(partyType);
            setPartyType = false;
        }
        partyForm.setDatasourcePartySubType(isPerson ? dirPartyTable_DirPerson_ds : dirPartyTable_DirOrganizationBase_ds);
        partyForm.manageFields();
    }
}

private void enableApplicableRelatedInfoButtons()
{
    ContactPerson contactPerson;
    select firstonly RecId from contactPerson
        where contactPerson.ContactForParty == smmQuotationCompetitorGroup.Party;

    smmContactsListPage.enabled(contactPerson.RecId != 0);

    smmQuotationCompetitors smmQuotationCompetitors;
    select firstonly RecId from smmQuotationCompetitors
        where smmQuotationCompetitors.Party == smmQuotationCompetitorGroup.Party &&
        smmQuotationCompetitors.RefTableId == tableNum(smmOpportunityTable);

    element.design().controlName(identifierStr(smmOpportunitiesListPage)).enabled(smmQuotationCompetitors.RecId != 0);
}

private void enableCompetitorPriceButton()
{
    retailCompetitorPriceButton.enabled(smmQuotationCompetitorGroup.RecId != 0);
}

public boolean isActive()
{
    return true;
}

