public void initValue()
{
    Common              callerRecord;

    super();

    if (element.args())
    {
        callerRecord = element.args().record();

        if (callerRecord.TableId == tableNum(DirPartyTable))
        {
            dirPartyTable.data(callerRecord);

            if (dirPartyTable.type() == DirPartyType::Person)
            {
                dirPersonName.data(DirPersonName::find(dirPartyTable.RecId));
            }
            smmQuotationCompetitorGroup.Party = dirPartyTable.RecId;
            smmQuotationCompetitorGroup.modifiedField(fieldNum(smmQuotationCompetitorGroup,Party));
            smmQuotationCompetitorGroup_ds.forceWrite(true);
        }
    }
}

public void executeQuery()
{
    QueryBuildDataSource    qbQuotationCompetitorGroupDataSource;
    QueryBuildDataSource    qbDirPartyTableDataSource;
    if (this.queryRun())
    {
        qbQuotationCompetitorGroupDataSource = this.queryRun().query().dataSourceTable(tableNum(smmQuotationCompetitorGroup));
        qbDirPartyTableDataSource = this.queryRun().query().dataSourceTable(tableNum(DirPartyTable));

        // If user wants to sort on Competitors , then sorting on DirPartyTable should be cleared
        if (sortedOnDirPartyTable && qbDirPartyTableDataSource.sortFieldCount()
            && qbQuotationCompetitorGroupDataSource.sortFieldCount())
        {
            qbDirPartyTableDataSource.sortClear();
            sortedOnDirPartyTable = false;
        }
        // If user wants to sort on DirPartyTable , then sorting on Competitors should be cleared
        else if (!sortedOnDirPartyTable && qbDirPartyTableDataSource.sortFieldCount()
                && (qbQuotationCompetitorGroupDataSource.sortFieldCount() ||
                    qbQuotationCompetitorGroupDataSource.sortIndexCount()))
        {
            qbQuotationCompetitorGroupDataSource.sortClear();
            sortedOnDirPartyTable = true;
        }
    }
    super();
}

public void delete()
{
    super();

    smmSwotTable_ds.executeQuery();
    element.enableCompetitorPriceButton();
}

public void write()
{
    ttsbegin;

    if (!dirPartyTable.RecId)
    {
        dirPartyTable_ds.write();
    }

    smmQuotationCompetitorGroup.Party = dirPartyTable.RecId;
    super();

    ttscommit;
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        partyForm.parmPartyType(dirPartyTable.type());
        element.updateControls();
        element.updateDesign();
        element.enableApplicableRelatedInfoButtons();
        element.enableCompetitorPriceButton();
        smmSwotTable_ds.executeQuery();
    }

    return ret;
}

public void removeFilter()
{
    // If sort is removed, then sort on DirPartyTable should be cleared
    this.queryRun().query().dataSourceTable(tableNum(DirPartyTable)).sortClear();
    sortedOnDirPartyTable = false;
    super();
}

public void init()
{
    super();

    element.enableCompetitorPriceButton();
}

public void executeQuery()
{
    swotRefTableIdQBR.value(queryValue(smmQuotationCompetitorGroup.TableId));
    swotRefRecIdQBR.value(queryValue(smmQuotationCompetitorGroup.RecId));

    super();

    smmSwotTable_ds.allowCreate(smmQuotationCompetitorGroup.RecId != 0);
}

public void init()
{
    super();

    //
    // The query on any related datasource should only depend on the currently
    // active competitor. So, clear any automaticaly created dynalinks.
    //
    this.query().dataSourceNo(1).clearDynalinks();

    //
    // Create a range for filtering on competitor.
    //
    swotRefTableIdQBR = this.query().dataSourceNo(1).addRange(fieldNum(smmSwotTable,RefTableId));
    swotRefRecIdQBR = this.query().dataSourceNo(1).addRange(fieldNum(smmSwotTable,RefRecId));

    this.query().dataSourceNo(1).clearDynalinks();
}

public void executeQuery()
{
    if (smmQuotationCompetitorGroup.RecId != 0)
    {
        partyIdRange.value(queryValue(smmQuotationCompetitorGroup.Party));
        refTableIdRange.value(queryValue(tableNum(smmOpportunityTable)));
    }

    super();
}

public void init()
{
    QueryBuildDataSource    qbds;
    super();

    qbds = this.query().dataSourceNo(1).addDataSource(tableNum(smmQuotationCompetitors));
    qbds.relations(true);
    partyIdRange = qbds.addRange(fieldNum(smmQuotationCompetitors, Party));
    refTableIdRange = qbds.addRange(fieldNum(smmQuotationCompetitors, RefTableId));
}

public display smmQuotationWinner winner(smmOpportunityTable _smmOpportunityTable)
{
    smmQuotationWinner      winner;
    smmQuotationCompetitors smmQuotationCompetitors;

    winner = (select QuotationWinner from smmQuotationCompetitors
        where smmQuotationCompetitors.Party == smmQuotationCompetitorGroup.Party &&
              smmQuotationCompetitors.RefTableId == _smmOpportunityTable.TableId &&
              smmQuotationCompetitors.RefRecId == _smmOpportunityTable.RecId).QuotationWinner;

    return winner;
}

