public boolean modified()
{
    boolean ret = super();

    if (lastPartyType == DirPartyType::None)
    {
        // This occurs on the initial open of the form.
        // We don't need to delete the competitor and party.
        return ret;
    }

    if (ret)
    {
        DirPartyType selected = any2Enum(typeComboBox.selection());

        if (selected != lastPartyType)
        {
            smmQuotationCompetitorGroup_ds.delete();
            partyForm.parmPartyType(selected);
            partyForm.create();
            lastPartyType = selected;
        }
    }

    return ret;
}

public void jumpRef()
{
    super();
    smmUtility::openMenuItemForm(menuitemDisplayStr(DirAddressBook), null, this);
}

public boolean modified()
{
    boolean ret;

    // Write data source before writing address books to party.
    dirPartyTable_ds.write();
    ret = super();

    if (ret)
    {                
        boolean forceWrite = smmQuotationCompetitorGroup_ds.forceWrite();
        // Set forceWrite to make form handler write address books to party record.
        smmQuotationCompetitorGroup_ds.forceWrite(true);
        partyForm.writeAddressBookParty();
        smmQuotationCompetitorGroup_ds.forceWrite(forceWrite);
        // Reread data source to reload the buffer with the changed address book associations.
        dirPartyTable_ds.reread();

    }

    return ret;
}

void clicked()
{
    super();
    partyForm.manageFields(true);
}
