void distributeByResponsibility(TableId _tableId)
{
    Dialog          d;
    Caption         caption;
    DialogField     dialogResponsibilities;
    int             numberOfUpdatedTargets;
    int             unassignedTargets;
    switch (_tableId)
    {
        case tableNum(smmBusRelTable) :
        caption = "@SYS82439"; // Distribute by responsibilites on business relaions
        break;

        case tableNum(CustTable) :
        caption = "@SYS108434"; // Distribute by responsibilites on customers
        break;

        case tableNum(VendTable) :
        caption = "@SYS108435"; // Distribute by responsibilites on vendors
        break;

        default :
        return;
    }
    d = new Dialog(caption);

    // Add a dialog field for selecting what responsibility the distribution should use
    dialogResponsibilities  = d.addField(extendedTypeStr(smmResponsibilityId));

    if (d.run())
    {
        // Check that responsibility field is filled out
        if (!dialogResponsibilities.value())
        {
            // A responsiblitiy criteria must be selected
            throw error("@SYS82440");
        }
        else
        {
            // Update target responsible employee based on the responsiblity setup on the relation
            numberOfUpdatedTargets = smmTMCallListGroup::distributeByResponsibilityOnRelation(_tableId,smmTMCallListGroup.CallListID, dialogResponsibilities.value(), false);

            if (numberOfUpdatedTargets > 0)
            {
                // Responsible employee on %1 call list targets are updated
                info(strFmt("@SYS86954", numberOfUpdatedTargets));
            }
            else
            {
                // No targets were updated
                info("@SYS87158");
            }

            // Find number of unassigned targets left on the campaign
            unassignedTargets = smmTMCallListTable::countNumbersOfUnassignedTargets(smmTMCallListGroup.CallListID);

            // Is there unassigned targets
            if (unassignedTargets > 0)
            {
                // There are still targets that are not assigned to a employee
                warning("@SYS87137");
            }

            if (numberOfUpdatedTargets > 0)
            {
                // Run query again to show the updated targets in the form
                smmTMCallListTable_ds.executeQuery();
            }
        }
    }
}

