public void delete()
{
    smmCampaignId campaignId = smmTMCallListGroup.campaignId();

    ttsbegin;

    element.numberSeqFormHandler().formMethodDataSourceDelete();

    super();

    // Should the status of the campaign be changed to Planning?
    if (campaignId && Box::yesNo("@SYS86979", DialogButton::Yes) == DialogButton::Yes)
    {
        smmCampaignTable::changeStatusOnCampaign(campaignId);
    }

    ttscommit;
}

public void linkActive()
{
    Common                  callerRecord;
    tableId                 callerTableId;
    smmResponsibilityGroup  responsibilityGroup;
    QueryBuildDataSource    queryBuildDataSource;

    element.numberSeqFormHandler().formMethodDataSourceLinkActive();

    // Is form called with a record
    if (element.args() && element.args().record())
    {
        callerTableId = element.args().dataset();
        switch (callerTableId)
        {
            // Is the form called from the business relation/customer/vendor form
            case tableNum(smmBusRelTable) :
            case tableNum(CustTable) :
            case tableNum(VendTable) :

            // Get the current selected business relation/customer/vendor record
            callerRecord  = element.args().record();

            if (callerTableId == tableNum(smmBusRelTable))
            {
                party = callerRecord.(fieldNum(smmBusRelTable,Party));
            }
            else if (callerTableId == tableNum(CustTable))
            {
                party = callerRecord.(fieldNum(CustTable,Party));
            }
            else if (callerTableId == tableNum(VendTable))
            {
                party = callerRecord.(fieldNum(VendTable,Party));
            }

            // Get the manual created call list datasource
            queryBuildDataSource = this.query().dataSourceTable(tableNum(smmTMCallListTable));

            // If the datasource isn't created already
            if (!queryBuildDataSource)
            {
                // Add the extra call list data source
                queryBuildDataSource    = this.query().dataSourceNo(1).addDataSource(tableNum(smmTMCallListTable));

                // Add a new range for filtering on business account
                rangeParty   = queryBuildDataSource.addRange(fieldNum(smmTMCallListTable, Party));

                // Link the new datasource to the existing query
                queryBuildDataSource.joinMode(JoinMode::ExistsJoin);
                queryBuildDataSource.fetchMode(QueryFetchMode::One2One);
                queryBuildDataSource.addLink(fieldNum(smmTMCallListGroup, CallListID), fieldNum(smmTMCallListTable, CallListId));

                // The new datasource should be hidden in the query selection form
                rangeParty.status(RangeStatus::Hidden);
            }

            // Filter on the current selected business relation
            rangeParty.value(queryValue(party));
            break;

            // Is the form called from activity form
            case tableNum(smmActivities) :
            smmActivityParentLink::updateQuery(this.query().dataSourceTable(tableNum(smmTMCallListGroup)), element.args().record());
            break;

            // Is form called from Responsibilities form
            case tableNum(smmResponsibilityGroup) :
            // Get the current selected responsibilities record
            responsibilityGroup  = element.args().record();

            // Get the manual created responsibilities assignment datasource
            queryBuildDataSource = this.query().dataSourceTable(tableNum(smmResponsibilitiesEmplTable));

            // If the datasource isn't created already
            if (!queryBuildDataSource)
            {
                // Add the extra responsibility assigment data source
                queryBuildDataSource    = this.query().dataSourceTable(tableNum(smmTMCallListGroup)).addDataSource(tableNum(smmResponsibilitiesEmplTable));

                // Link the new datasource to the existing query
                queryBuildDataSource.joinMode(JoinMode::ExistsJoin);
                queryBuildDataSource.fetchMode(QueryFetchMode::One2One);
                queryBuildDataSource.addLink(fieldNum(smmTMCallListGroup, RecId), fieldNum(smmResponsibilitiesEmplTable, RefRecId));
            }

            rangeRefTableId   = queryBuildDataSource.findRange(fieldNum(smmResponsibilitiesEmplTable, RefTableId));
            if (!rangeRefTableId)
            {
                rangeRefTableId   = queryBuildDataSource.addRange(fieldNum(smmResponsibilitiesEmplTable, RefTableId));
            }
            rangeRefTableId.status(RangeStatus::Hidden);

            rangeRespId       = queryBuildDataSource.findRange(fieldNum(smmResponsibilitiesEmplTable, ResponsibilityId));
            if (!rangeRespId)
            {
                rangeRespId   = queryBuildDataSource.addRange(fieldNum(smmResponsibilitiesEmplTable, ResponsibilityId));
            }
            rangeRespId.status(RangeStatus::Hidden);

            // Filter on the responsibilities assigments for call lists, selected responsibility
            rangeRefTableId.value(queryValue(tableNum(smmTMCallListGroup)));
            rangeRespId.value(queryValue(responsibilityGroup.ResponsibilityId));
            break;
        }
    }
    super();
}

public boolean validateWrite()
{
    smmTMCallPlannedDateTime   firstPlannedDate = smmTMCallListGroup.firstTargetPlannedDate();
    smmTMCallPlannedDateTime   lastPlannedDate  = smmTMCallListGroup.lastTargetPlannedDate();
    boolean     ret;

    ret = super();

    ret = element.numberSeqFormHandler().formMethodDataSourceValidateWrite(ret) && ret;

    // This code must be executed last in the valiadte method
    if (ret)
    {
        // Are there targets that are planned outside the new call list period
        if ((firstPlannedDate != utcDateTimeNull() && smmTMCallListGroup.FromDateTime > firstPlannedDate) ||
            (lastPlannedDate  != utcDateTimeNull() && smmTMCallListGroup.ToDateTime < lastPlannedDate))
        {
            // There are targets with planned date before or after the new call list period. Change dates on the targets?
            if (Box::yesNo("@SYS89041", DialogButton::Yes) == DialogButton::Yes)
            {
                // Set planned date to from date for the targets that were planned before the new from date
                smmTMCallListGroup::setNewFromDateOnTargets(smmTMCallListGroup.CallListID, smmTMCallListGroup.FromDateTime);

                // Set planned date to to date for the targets that were planned before the new to date
                smmTMCallListGroup::setNewToDateOnTargets(smmTMCallListGroup.CallListID, smmTMCallListGroup.ToDateTime);
            }
            else
            {
                ret = false;
            }
        }
    }

    return ret;
}

public void write()
{
    smmActivities   smmActivities;

    ttsbegin;
    element.numberSeqFormHandler().formMethodDataSourceWrite();

    try
    {
        smmTMCallListGroup.updateFromAdvPromptActivity(smmTMCallListGroup.orig());
        super();
    }
    catch
    {
        if (smmTMCallListGroup.ActivityNumber
            && smmTMCallListGroup.orig().ActivityNumber != smmTMCallListGroup.ActivityNumber)
        {
            ttsbegin;
            smmTMCallListGroup.ActivityNumber = smmTMCallListGroup.orig().ActivityNumber;
            smmActivities = smmActivities::find(smmTMCallListGroup.ActivityNumber,true);
            if (smmActivities)
            {
                smmActivities.delete(false);
            }
            ttscommit;
        }
    }
    ttscommit;
}

