public boolean validate()
{
    boolean             ret;
    smmTMCallListTable  smmTMCallListTable_Orig;

    smmTMCallListTable_Orig = smmTMCallListTable.orig();
    ret = super();

    // Warn user that attached activity will be deleted
    if (ret && !smmTMCallListTable.CallRespWorker && smmTMCallListTable_Orig.CallRespWorker && smmTMCallListTable.CallbackActivityId
        && smmTMCallListTable.CallStatus != smmTMCallStatus::CallBack)
    {
        ret = (DialogButton::Ok == Box::okCancel("@SYS90963",DialogButton::Ok));
    }

    return ret;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Common ret;
    DirPartyTable selectedRecord;
    ContactPerson contactPerson = ContactPerson::find(smmTMCallListTable.ContactPersonId);

    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(DirPartyTable),
                                                                                    _formReferenceControl, true);
    Query lookupQuery;

    if (contactPerson.ContactForParty)
    {
        sysTableLookup.addLookupfield(fieldNum(DirPartyTable, Name));

        // Create a custom Query that filters and displays just the person titles
        lookupQuery = new Query();
        lookupQuery.addDataSource(tableNum(DirPartyTable)).addRange(fieldNum(DirPartyTable, RecId)).value(SysQuery::value(contactPerson.ContactForParty));
        sysTableLookup.parmQuery(lookupQuery);

        selectedRecord = sysTableLookup.performFormLookup();

        ret = selectedRecord;
    }
    else
    {
        ret = super(_formReferenceControl);
    }

    return ret;
}

public boolean validate()
{
    boolean ret;
    MCRsmmBusRelTable mcrsmmBusRelTable;

    ret = super();
    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
    {
        mcrsmmBusRelTable = MCRsmmBusRelTable::findParty(smmTMCallListTable.Party);
        // Checks if the business relation was merged, and if so errors; only surviving record can be updated.
        if (mcrsmmBusRelTable.MergedParent)
        {
            ret = checkFailed(strfmt("@MCR12337", mcrsmmBusRelTable.smmBusRelTable().BusRelAccount, mcrsmmBusRelTable.MergedParent));
        }

        if (mcrsmmBusRelTable.IsListDeleted)
        {
            ret = checkFailed("@MCR26131");
        }
    }

    return ret;
}

