    else
    {
        // No targets were updated
        info("@SYS87158");
    }

    // Find number of unassigned targets left on the campaign
    unassignedTargets = smmTMCallListTable::countNumbersOfUnassignedTargets(smmTMCallListGroup.CallListID);

    // Is there unassigned targets
    if (unassignedTargets > 0)
    {
        // There are still targets that are not assigned to a employee
        warning("@SYS87137");
    }

    if (numberOfUpdatedTargets > 0)
    {
        // Run query again to show the updated targets in the form
