void clicked()
{
    if (smmTMCallListTable.CallStatus == smmTMCallStatus::Closed ||
        !smmTMCallListTable.RecId)
    {
        setCallBack.enabled(false);
        setCancelled.enabled(false);
        setClosed.enabled(false);
    }
    else
    {
        setCallBack.enabled(true);
        setCancelled.enabled(true);
        setClosed.enabled(true);
    }

    if (smmTMCallListTable.CallStatus == smmTMCallStatus::CallBack)
    {
        setCallBack.enabled(false);
    }

    if (smmTMCallListTable.CallStatus == smmTMCallStatus::Cancelled)
    {
        setCancelled.enabled(false);
    }

    super();
}

