void clicked()
{
    smmLeadTable        smmLeadTable;
    smmOpportunityTable smmOpportunityTable;
    CustAccount         custAccount;
    smmBusRelAccount    busRelAccount;
    custAccount = CustTable::findByPartyRecId(smmTMCallListTable.Party).AccountNum;
    busRelAccount = smmBusRelTable::findByParty(smmTMCallListTable.Party).BusRelAccount;

    custTableButton.enabled(custAccount ? true : false);
    vendTableButton.enabled(DirPartyTableHelper::isvendor(smmTMCallListTable.Party));
    smmBusRelTableButton.enabled(DirPartyTableHelper::isRelation(smmTMCallListTable.Party));
    smmActivitiesButton.enabled(smmTMCallListTable.CallbackActivityId ? true : false);
    smmQuotationCompetitorGroupButton.enabled(smmQuotationCompetitorGroup::find(smmTMCallListTable.Party) ? true : false);

    salesQuotationTableButton.enabled(
    (custAccount && SalesQuotationTable::existSales_custAccount(custAccount)) ||
    (busRelAccount && SalesQuotationTable::existSales_BusRelAccount(busRelAccount)));

    salesQuotationProjTableButton.enabled(
    (custAccount && SalesQuotationTable::existProject_custAccount(custAccount)) ||
    (busRelAccount && SalesQuotationTable::existProject_BusRelAccount(busRelAccount)));

    select firstonly RecId from smmLeadTable where smmLeadTable.Party == smmTMCallListTable.Party;
    smmLeadTableButton.enabled(smmLeadTable.RecId ? true : false);

    select firstonly RecId from smmOpportunityTable where smmOpportunityTable.Party == smmTMCallListTable.Party;
    smmOpportunityTableButton.enabled(smmOpportunityTable.RecId ? true : false);

    super();
}

public void lookup()
{
    Args    args;
    FormRun formRun;
    ContactPerson   contactPerson;

    args = new Args();
    args.name(formStr(ContactPersonLookup));
    args.caller(contactPersonId);
    args.lookupValue(smmTMCallListTable.ContactPersonId);

    formRun = classfactory.formRunClass(args);
    formRun.party(smmTMCallListTable.Party);
    formRun.callingNameControl(contactPersonName);
    formRun.callingIdControl(this);
    formRun.init();

    formRun.selectRecordMode(contactPersonId);
    contactPersonId.performFormLookup(formRun);
    formRun.wait();

    if (formRun.closedOk())
    {
        contactPerson = formRun.selectRecordModeSelectedRecord();

        smmTMCallListTable.ContactPersonId = contactPerson.ContactPersonId;
        contactPersonId.text(contactPerson.ContactPersonId);
        smmTMCallListTable.Party = contactPerson.ContactForParty;
        contactPersonName.enabled(false);
        referenceGroup.enabled(false);
    }
}

public void lookup()
{
    contactPersonId.lookup();
}

