[Form]
public class smmTMCallListTable extends FormRun
{
    QueryBuildRange             typeFilterQBR;
    QueryBuildRange             statusFilterQBR;
    QueryBuildRange             emplFilterQBR;

    boolean                     lookupFlag;
    boolean                     showAll;
    boolean                     calling;

    smmCallId                   callId;
    boolean                     onHoldFlag;
    boolean                     timerFlag;

    smmTMCallListTable          callListToConnectToDocument;
    boolean                     documentFormOpened;
    FormDataSource              formDataSource;
    HcmWorkerLookup             hcmWorkerLookupCurrentCompany;

}

void atCallFinished()
{
    smmTMActivityVariables  tmActivityVariables = new smmTMActivityVariables();

    tmActivityVariables.setOpenedByMenuFunction(true);

    smmUtility::openTMSetCallProperties(smmTMCallListTable, tmActivityVariables);
}

void changeStatusClosed()
{
    smmTMActivityVariables smmTMActivityVariables = new smmTMActivityVariables();

    smmTMActivityVariables.setChangeClosed(true);

    smmUtility::openTMSetCallProperties(smmTMCallListTable, smmTMActivityVariables);
}

smmCallId getCallId()
{
    return callId;
}

boolean getOnHoldStatus()
{
    return onHoldFlag;
}

void init()
{
    smmTMCallListGroup  callerCallListGroup;

    lookupFlag = false;

    super();

    timerFlag = true;
    calling   = false;

    // If form is not opened from an other datasource, ok to filter on employee.
    if (!element.args().record())
    {
        ctrlshowAll.value(false);
        workerFilter.value(smmUtility::getCurrentContactWorker());
    }
    else
    {
        //To get formdatasource of caller form
        formDataSource = FormDataUtil::getFormDataSource(element.args().record());

        // Is the form called with a call list group
        if (element.args().dataset() == tableNum(smmTMCallListGroup))
        {
            // Call list filter is disabled
            callListIDFilter.enabled(false);

            // The call list filter is set to the active call list from the caller form
            callerCallListGroup = element.args().record();
            callListIDFilter.text(callerCallListGroup.CallListID);
        }

        ctrlshowAll.value(true);
    }

    hcmWorkerLookupCurrentCompany = HcmWorkerLookup::newCustomOptions(true, true, true, false, true, true, true, true, true);
}

public void rereadDatasource()
{
    // Refresh the call list data source from another form
    smmTMCallListTable_ds.reread();
}

void setActiveButtons()
{
    // Activity button should only be active if the call has a call back activity
    buttonActivities.enabled(smmTMCallListTable.CallbackActivityId != '');
}

void setShowAllStatus()
{
    if (ctrlShowAll.value())
    {
        ctrlToBeCalled.value(0);
    }

    if (!ctrlShowAll.value() && !ctrlToBeCalled.value())
    {
        statusFilterControl.allowEdit(true);
    }
    else
    {
        statusFilterControl.allowEdit(false);
    }

    smmTMCallListTable_ds.executeQuery();
}

void setShowTobeCalledStatus()
{
    if (ctrlToBeCalled.value())
    {
        ctrlShowAll.value(0);
    }

    if (!ctrlShowAll.value() && !ctrlToBeCalled.value())
    {
        statusFilterControl.allowEdit(true);
    }
    else
    {
        statusFilterControl.allowEdit(false);
    }

    smmTMCallListTable_ds.executeQuery();
}

public void reread()
{
    super();
    // If form is called from other datasource - refresh the caller datasource
    if (formDataSource)
    {
        formDataSource.research();
        formDataSource.reread();
    }
}

int active()
{
    int ret;

    ret = super();

    // Enable questionnaire button if questionnaire is set for the call list
    buttonKMQuestionnaireRun_Win32_CallList.enabled(smmTMCallListTable.showQuestionnaire() != '');

    // Disable questionnaire button if call is closed or cancelled
    if (smmTMCallListTable.CallStatus == smmTMCallStatus::Closed || smmTMCallListTable.CallStatus == smmTMCallStatus::Cancelled)
    {
        buttonKMQuestionnaireRun_Win32_CallList.enabled(false);
    }

    if (smmTMCallListTable.CallStatus == smmTMCallStatus::Closed)
    {
        setCallBack.enabled(false);
        setCancelled.enabled(false);
        setClosed.enabled(false);
    }
    else
    {
        setCallBack.enabled(true);
        setCancelled.enabled(true);
        setClosed.enabled(true);
    }

    if (smmTMCallListTable.CallStatus == smmTMCallStatus::CallBack)
    {
        setCallBack.enabled(false);
    }

    if (smmTMCallListTable.CallStatus == smmTMCallStatus::Cancelled)
    {
        setCancelled.enabled(false);
    }

    buttonActivities.enabled(smmTMCallListTable.CallbackActivityId != '');

    return ret;
}

void executeQuery()
{
    #define.space(' ')
    #define.comma(',')

    // Set filter on selected employee
    emplFilterQBR.value(workerFilter.value()?queryValue(workerFilter.value()):SysQuery::valueUnlimited());

    // Set call list filter
    typeFilterQBR.value(callListIdFilter.text() != ''?queryValue(callListIdFilter.text()):SysQuery::valueUnlimited());

    // Set buttons for call status
    setCallBack.enabled(false);
    setCancelled.enabled(false);
    setClosed.enabled(false);

    // If show all checkbox is checked
    if (ctrlShowall.value() == NoYes::Yes)
    {
        // Clear the call list status filter
        statusFilterQBR.value(#space);
    }
    else
    // Is the "to be called" checkboc checked
    if (ctrlToBeCalled.value() == NoYes::Yes)
    {
        // Filter on call with status "Open" and "Call back"
        statusFilterQBR.value(queryValue(smmTMCallStatus::Open) + #comma + queryValue(smmTMCallStatus::CallBack));
    }
    else
    {
        // Filter on the selected call list status
        statusFilterQBR.value(queryValue(statusFilterControl.selection()));
    }

    super();
}

public void linkActive()
{
    smmTMCallListGroup  callerCallListGroup;

    super();

    if (element.args())
    {
        if (element.args().dataset() == tableNum(smmTMCallListGroup))
        {
            // The call list filter is set to the active call list from the caller form
            callerCallListGroup = element.args().record();
            callListIDFilter.text(callerCallListGroup.CallListID);
        }
    }
}

void init()
{
    super();

    // Enable caching of the document handling, directory and contact person name
    smmTMCallListTable_ds.cacheAddMethod(tableMethodStr(smmTMCallListTable, showDocHanIcon));
    smmTMCallListTable_ds.cacheAddMethod(tableMethodStr(smmTMCallListTable, showDirName));
    smmTMCallListTable_ds.cacheAddMethod(tableMethodStr(smmTMCallListTable, showContactPersonName));

    typeFilterQBR     = this.query().dataSourceNo(1).addRange(fieldNum(smmTMCallListTable, CallListId));
    statusFilterQBR   = this.query().dataSourceNo(1).addRange(fieldNum(smmTMCallListTable, CallStatus));
    emplFilterQBR     = this.query().dataSourceNo(1).addRange(fieldNum(smmTMCallListTable, CallRespWorker));
    emplFilterQBR.status(RangeStatus::Hidden);
}

void clicked()
{
    super();

    smmTMCallListTable_ds.active();
}

void clicked()
{
    element.changeStatusClosed();

    super();
}

/// <summary>
/// Requires the user to convert the prospect to a customer
/// if the business relation is a prospect.
/// </summary>
void clicked()
{
    smmBusRelTable      smmBusRelTable;
    NoYes               conversionSuccesfull;

    smmBusRelTable = smmBusRelTable::findByParty(smmTMCallListTable.Party);

    if (smmBusRelTable.RecId == 0
        || smmBusRelTable.VendAccount())
    {
        // do nothing
        return;
    }
    // BR is a prospect & not flagged as deleted
    else if (!smmBusRelTable.CustAccount()
            && !smmBusRelTable.mcrIsListDeleted())
    {
        // BR is a prospect type - ask if the user wants to convert to a Customer
        // and if they choose no then they cannot be allowed to see/create SO's for
        // the contact.
        if (box::yesNo("@MCR25854",dialogbutton::No, "@MCR25855") == DialogButton::Yes)
        {
            // Convert to customer
            conversionSuccesfull = smmBusRelTable::convert2Customer(smmBusRelTable);

            if (conversionSuccesfull)
            {
                smmBusRelTable::MCROpenCustTable(smmBusRelTable);
            }
        }
        else // user choose not to convert the prospect to a customer - do nothing
        {
            return;
        }
    }

    // call the sales order form
    super();
}

