public void init()
{
    ;
    super();
    this.setFrameTypes();
    this.deleteData();
    this.insertData();
}

void setFrame()
{
    int i;
    FormGroupControl    c;
    ;

    tbpFrameTypes.setFrameTypes();
}

void setFrameOption(FormFrameOptionButton _value)
{
    int i;
    FormGroupControl    c;
    ;
    for (i=1; i<=grpFrameTypes.controlCount(); i++)
    {
        c = grpFrameTypes.controlNum(i);
        c.frameOptionButton(_value);
    }
}

void setFrameTypes()
{
    int i;
    FormGroupControl    c;
    DictEnum    dictEnum;
    ;

    dictEnum = new DictEnum(enumnum(FormFrameType));
    for (i=1; i<=grpFrameTypes.controlCount(); i++)
    {
        c = grpFrameTypes.controlNum(i);
        c.frameType(i-1);
        c.caption(dictEnum.value2Symbol(i-1));
    }
}

void insertData()
{
    tutorial_Form_groupOption dataTable;

    dataTable.Name = "Tom Hanks";
    dataTable.Description = "A very good actor.";
    dataTable.insert();

    dataTable.Name = "Lucille Ball";
    dataTable.Description = "Hilarious lady.";
    dataTable.insert();

    dataTable.Name = "David Letterman";
    dataTable.Description = "Late night tv host.";
    dataTable.insert();
}

void deleteData()
{
    tutorial_Form_groupOption dataTable;

    ttsbegin;

    delete_from dataTable where dataTable.Name == "Tom Hanks";
    delete_from dataTable where dataTable.Name == "David Letterman";
    delete_from dataTable where dataTable.Name == "Lucille Ball";

    ttscommit;
}

void setFrameTypes()
{
    int i;
    FormGroupControl    c;
    DictEnum    dictEnum;
    ;

    dictEnum = new DictEnum(enumnum(FormFrameType));
    for (i=1; i<this.controlCount(); i++)
    {
        c = this.controlNum(i);
        c.frameType(i-1);
        c.caption(dictEnum.value2Symbol(i-1));
    }
}

public int selectionChange()
{
    int ret;
    ret = super();
    element.setFrameOption(this.selection());

    return ret;
}
