/// <summary>
///    Initializes the <c>AssetParameters</c> table buffer.
/// </summary>
public void initValue()
{
    const int ICMSCreditInstallments = 48;
    const int DepreciationMin = 10;        

    super();

    this.DepreciationMin = DepreciationMin;
    this.AssetAllowAcqPostingPO = NoYes::Yes;
    this.DepreciationMinReportingCurrency = DepreciationMin;

    this.IsDisposalSameYear = NoYes::Yes;

    this.MaxBooksInAcquisitionProposalJournal = FixedAssetsConstants::MaxBooksInAcquisitionProposalJournal;
    this.MaxBooksInDepreciationProposalJournal = FixedAssetsConstants::MaxBooksInDepreciationProposalJournal;
    this.MaxBooksInSumDepreciationProposalJournal = FixedAssetsConstants::MaxBooksInSumDepreciationProposalJournal;

    if (BrazilParameters::isEnabled())
    {
        this.icmsCreditInstallments_BR = ICMSCreditInstallments;
    }
}

public void modifiedField(fieldId _fieldId)
{
    super(_fieldId);

    switch (_fieldId)
    {
        case fieldNum(AssetParameters, AssetAllowCreate):
            if (!this.AssetAllowCreate)
            {
                this.AssetAllowAutoCreate = NoYes::No;
            }
            break;

        case fieldNum(AssetParameters, AssetBookInSingleUse):
            AssetBookInUseForPosting::deleteAssetBooksInSingleUse();
            break;
    }
}

public AssetPostingProfile postingProfile()
{
    if (!this.PostingProfile)
    {
        throw error("@SYS67359");
    }

    return this.PostingProfile;
}

