public boolean validateField(FieldId p1)
{
    boolean                     ret;
    AssetBookTable              assetBookTable;
    AssetBook                   assetBook;
    AssetGroupBookSetup         assetGroupBook;
    AssetDepreciationProfile    assetDepreciationProfile;

    // <GIN>
    boolean                     isCompanyActEnabled = AssetParameters::isDepreciationCompaniesActEnable_IN();
    boolean                     isIncomeTaxActEnabled = AssetParameters::isDepreciationIncomeTaxActEnable_IN();

    #define.MaxValue(9999999999.99)
    #define.MaxDays(999)
    // </GIN>

    ret = super(p1);

    switch (p1)
    {
        case fieldNum(AssetParameters,BarcodeEqualsAssetNumber):
            if (this.BarcodeEqualsAssetNumber)
            {
                info("@SYS67377");
            }
            break;
        case fieldNum(AssetParameters, HalfYearDepreciation_AT):

            // This code is only executed if the Austria configuration key is enabled.
            // If a user checks the checkbox for Depreciation,
            // verify that the Asset Book and Asset Group Book records.
            if (this.HalfYearDepreciation_AT)
            {
                // Verify that there is not any Asset book that has an Alternative Profile
                // or Primary profile with a Straight Line Life Remaining depreciation method
                // and the Half Year Start Year convention.
                select count(RecId) from assetBook
                join assetDepreciationProfile
                    where ((assetBook.DepreciationProfile == assetDepreciationProfile.Profile && assetBook.DepreciationAltProfile)
                        || (assetBook.DepreciationAltProfile == assetDepreciationProfile.Profile))
                        && assetBook.DepreciationConvention == AssetDepreciationConvention::HalfYearStart
                        && assetDepreciationProfile.Method == AssetDepreciationMethod::StraightLineLifeRemaining;

                if (assetBook.RecId > 0)
                {
                   // Half year deprecation cannot be enabled as there are fixed assets
                   // that have alternative depreciation profiles assigned to them.
                   ret = checkFailed("@FixedAssets:AssetWarningCannotApplySpecificRulesForHalfYearDepreciation_FixedAssets");
                }

                // Verify that there is not any Asset Group Book that has an Alternative Profile
                // or Primary Profile with a Straight Line Life Remaining depreciation method
                // and the Half Year Start Year convention.
                select count(RecId) from assetGroupBook
                join assetDepreciationProfile
                    where ((assetGroupBook.DepreciationProfile == assetDepreciationProfile.Profile && assetGroupBook.DepreciationAltProfile)
                        || (assetGroupBook.DepreciationAltProfile == assetDepreciationProfile.Profile))
                        && assetGroupBook.DepreciationConvention == AssetDepreciationConvention::HalfYearStart
                        && assetDepreciationProfile.Method == AssetDepreciationMethod::StraightLineLifeRemaining;

                if (assetGroupBook.RecId > 0)
                {
                   // Half year deprecation cannot be enabled as there are fixed assets
                   // groups that have alternative depreciation profiles assigned to them.
                   ret = checkFailed("@FixedAssets:AssetWarningCannotApplySpecificRulesForHalfYearDepreciation_FixedAssetGroups");
                }
            }
        break;

        // <GIN>
        case fieldNum(AssetParameters,ThresholdPercentage_IN):
            if (isIncomeTaxActEnabled
                && this.ThresholdPercentage_IN > 100)
            {
                this.ThresholdPercentage_IN = 100;
                info("@GLS63881");
            }
            break;

        case fieldNum(AssetParameters,MinNonSeasonalWorkingDays_IN):
            if (isCompanyActEnabled)
            {
                this.MinNonSeasonalWorkingDays_IN = max(0,min(#MaxDays,this.MinNonSeasonalWorkingDays_IN));
            }
            break;

        case fieldNum(AssetParameters,MinSeasonalWorkingDays_IN):
            if (isCompanyActEnabled)
            {
                this.MinSeasonalWorkingDays_IN = max(0,min(#MaxDays,this.MinSeasonalWorkingDays_IN));
            }
            break;

        case fieldNum(AssetParameters,MaxAcquisitionValue_IN):
            if (isCompanyActEnabled
                && this.MaxAcquisitionValue_IN > #MaxValue)
            {
                this.MaxAcquisitionValue_IN =  0;
            }
            break;
        // </GIN>

        case fieldNum(AssetParameters, MaxBooksInAcquisitionProposalJournal):
            if (this.MaxBooksInAcquisitionProposalJournal < FixedAssetsConstants::MaxBooksInAcquisitionProposalJournal)
            {
                this.MaxBooksInAcquisitionProposalJournal =  FixedAssetsConstants::MaxBooksInAcquisitionProposalJournal;
                warning(strFmt("@FixedAssets:FA_MaxBooksInAcquisitionProposalJournalValidation", FixedAssetsConstants::MaxBooksInAcquisitionProposalJournal));
            }
            break;

        case fieldNum(AssetParameters, MaxBooksInDepreciationProposalJournal):
            if (this.MaxBooksInDepreciationProposalJournal < FixedAssetsConstants::MaxBooksInDepreciationProposalJournal)
            {
                this.MaxBooksInDepreciationProposalJournal =  FixedAssetsConstants::MaxBooksInDepreciationProposalJournal;
                warning(strFmt("@FixedAssets:FA_MaxBooksInDepreciationProposalJournalValidation", FixedAssetsConstants::MaxBooksInDepreciationProposalJournal));
            }
            break;

        case fieldNum(AssetParameters, MaxBooksInSumDepreciationProposalJournal):
            if (this.MaxBooksInSumDepreciationProposalJournal < FixedAssetsConstants::MaxBooksInSumDepreciationProposalJournal)
            {
                this.MaxBooksInSumDepreciationProposalJournal =  FixedAssetsConstants::MaxBooksInSumDepreciationProposalJournal;
                warning(strFmt("@FixedAssets:FA_MaxBooksInSumDepreciationProposalJournalValidation", FixedAssetsConstants::MaxBooksInSumDepreciationProposalJournal));
            }
            break;
    }

    return ret;
}

/// <summary>
/// Validates field value.
/// </summary>
/// <param name="_fieldName">
/// The field name.
/// </param>
/// <param name="_arrayIndex">
/// The array index.
/// </param>
/// <returns>
/// true if the validation succeed; otherwise, false.
/// </returns>
public boolean validateFieldValue(FieldName _fieldName, int _arrayIndex = 1)
{
    boolean ret;

    ret = super(_fieldName, _arrayIndex);

    if (ret)
    {
        switch (_fieldName)
        {
            // <GCN>
            case fieldStr(AssetParameters, EnableDepreciationMethods):
                if (!this.EnableDepreciationMethods
                    && AssetBookTable::existAssetBookForDepreciationMethods_CN())
                {
                    ret = checkFailed("@GLS61014");
                }
                break;

            case fieldStr(AssetParameters, EnableDepreciationPolicy):
                if (!this.EnableDepreciationPolicy
                    && AssetBookTable::existAssetBookForDepreciationPolicy_CN())
                {
                    ret = checkFailed("@GLS61015");
                }
                break;
            // </GCN>
        }
    }

    return ret;
}

