/// <summary>
/// Checks whether the user can create an asset acquisition transaction.
/// </summary>
/// <returns>
/// true if the user can create an asset acquisition transaction; otherwise, false.
/// </returns>
/// <remarks>
///  A user can create an asset acquisition if the following criteria are met:
///  <list type="bullet">
///   <item>
///    <description>
///    The <c>AssetAllowAcqPostingPO</c> value of the <c>AssetParamters</c> table is set to true.
///    </description>
///   </item>
///   <item>
///   <description>The user is a member of a group that can post asset transactions.</description>
///   </item>
///  </list>
/// </remarks>
public static boolean canUserCreateAssetAcquisition()
{
    boolean ret;
    SysGroup assetGroupPostPO;
    AssetParameters assetParameters;
    ;

    assetParameters = AssetParameters::find();
    ret = assetParameters.AssetAllowAcqPostingPO;
    if (ret)
    {
        assetGroupPostPO =  assetParameters.AssetUserGroupIdPostPO;
        if (assetGroupPostPO)
        {
            ret = UserInfoHelp::userInUserGroup(curUserId(), assetGroupPostPO);
        }
    }

    return ret;
}

/// <summary>
/// Check for India specific Asset parameters.
/// </summary>
/// <returns>
/// True if India specific Asset parameters are checked.
/// </returns>
public static boolean checkAssetParameters_IN()
{
    AssetParameters     assetParameters;

    if (!SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        return false;
    }

    assetParameters = AssetParameters::find();

    return assetParameters.DepreciationCompaniesAct_IN || assetParameters.DepreciationIncomeTaxAct_IN;
}

public static AssetParameters find( boolean _forUpdate = false,
                                    ConcurrencyModel _concurrencyModel = ConcurrencyModel::Auto)
{
    AssetParameters assetParameters, newParameter;
    int loopOnce = 0;
    ;
    //
    // This method returns the record if it is found, otherwise creates a record and returns it.
    // We need a looping variable because if the record doesn't exist, a new record is created for
    // first time in the while loop and we should loop for one more time to set the selectForUpdate
    // correctly. If record is found first time, then the while loop breaks.
    //
    while (!assetParameters && loopOnce <=1)
    {
        assetParameters.selectForUpdate(_forUpdate);

        if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
        {
            assetParameters.concurrencyModel(_concurrencyModel);
        }

        select firstonly assetParameters
            index Key
            where assetParameters.Key == 0;

        if (!assetParameters)
        {
            Company::createParameter(newParameter);
        }
        else
        {
            break;
        }
        loopOnce++;
    }

    return assetParameters;
}

