/// <summary>
///    Checks whether a fixed asset transaction of a particular <c>AssetTransTypeJournal</c> transaction
///    type requires a reason code before it can be saved.
/// </summary>
/// <param name="_assetTransType">
///    The value that will be checked.
/// </param>
/// <returns>
///    true if the specific <c>AssetTransTypeJournal</c> type is set to require a reason code; otherwise,
///    false.
/// </returns>
public static boolean transTypeRequiresValidation(AssetTransTypeJournal _assetTransType)
{
    boolean ret;
    ;
    switch (_assetTransType)
    {
        case AssetTransTypeJournal::Acquisition:
                 ret = AssetParameters::find().Acquisition == NoYes::Yes;
                 break;
        case AssetTransTypeJournal::AcquisitionAdj:
                 ret = AssetParameters::find().AcquisitionAdj  == NoYes::Yes;
                 break;
        case AssetTransTypeJournal::CapitalReserve:
                 ret = AssetParameters::find().CapitalReserve  == NoYes::Yes;
                 break;
        case AssetTransTypeJournal::CapitalReserveTransfer:
                 ret = AssetParameters::find().CapitalReserveTransfer == NoYes::Yes;
                 break;
        case AssetTransTypeJournal::Depreciation:
                 ret = AssetParameters::find().Depreciation == NoYes::Yes;
                 break;
        case AssetTransTypeJournal::DepreciationAdj:
                 ret = AssetParameters::find().DepreciationAdj == NoYes::Yes;
                 break;
        case AssetTransTypeJournal::DerogatoryDecrease:
                 ret = AssetParameters::find().DerogatoryDecrease  == NoYes::Yes;
                 break;
        case AssetTransTypeJournal::DerogatoryIncrease:
                 ret = AssetParameters::find().DerogatoryIncrease  == NoYes::Yes;
                 break;
        case AssetTransTypeJournal::DisposalSale:
                 ret = AssetParameters::find().DisposalSale  == NoYes::Yes;
                 break;
        case AssetTransTypeJournal::DisposalScrap:
                 ret = AssetParameters::find().DisposalScrap  == NoYes::Yes;
                 break;
        case AssetTransTypeJournal::ExtraordinaryDepreciation:
                 ret = AssetParameters::find().ExtraOrdinaryDepreciation  == NoYes::Yes;
                 break;
        case AssetTransTypeJournal::Revaluation:
                 ret = AssetParameters::find().Revaluation  == NoYes::Yes;
                 break;
        case AssetTransTypeJournal::WriteDownAdj:
                 ret = AssetParameters::find().WriteDownAdj  == NoYes::Yes;
                 break;
        case AssetTransTypeJournal::WriteUpAdj:
                 ret = AssetParameters::find().WriteUpAdj  == NoYes::Yes;
                 break;
        default:
                ret = false;
                break;
    }
    return ret;
}

