/// <summary>
/// Displays a bank name.
/// </summary>
/// <returns>
/// The bank name to display.
/// </returns>
public display Name dispBankName()
{
    return BankAccountTable::find(this.BankAccountTable).Name;
}

/// <summary>
/// Validates the field value.
/// </summary>
/// <param name="_fieldName">
/// The field name.
/// </param>
/// <param name="_arrayIndex">
/// The array index.
/// </param>
/// <returns>
/// true if the validation succeed; otherwise, false.
/// </returns>
public boolean validateFieldValue(FieldName _fieldName, int _arrayIndex = 1)
{
    boolean ret;

    ret = super(_fieldName, _arrayIndex);

    switch (_fieldName)
    {
        case fieldStr(BankStatementTransactionTypeHeader, BankAccountTable)    :
            ret = BankAccountTable::checkIsAdvancedBankReconEnabled(this.BankAccountTable);
            break;
    }

    return ret;
}

/// <summary>
/// Finds a specified record in the <c>BankStatementTransactionTypeHeader</c> table.
/// </summary>
/// <param name="_recId">
/// The record ID of a <c>BankStatementTransactionTypeHeader</c> record.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
/// The concurrency model option to use when the record is read; optional.
/// </param>
/// <returns>
/// A record in the <c>BankStatementTransactionTypeHeader</c> table; otherwise, an empty record.
/// </returns>
public static BankStatementTransactionTypeHeader find(
    RecId               _recId,
    boolean             _forUpdate = false,
    ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    BankStatementTransactionTypeHeader header;

    header.selectForUpdate(_forUpdate);

    if (_forUpdate
        && _concurrencyModel != ConcurrencyModel::Auto)
    {
        header.concurrencyModel(_concurrencyModel);
    }

    select firstonly header
        where header.RecId == _recId;

    return header;
}
