/// <summary>
/// Retrieves the <c>Name</c> attribute of <c>DimensionAttribute</c> table
/// </summary>
/// <param name="_set">
/// A boolean value
/// </param>
/// <param name="_brazilParameters">
/// A <c>BrazilParameters</c> table buffer
/// </param>
/// <param name="_name">
/// A <c>Name</c> attribute
/// </param>
/// <returns>
/// The <c>Name</c> attribute of <c>DimensionAttribute</c> table
/// </returns>
public edit Name dimensionAttributeName(boolean _set, BrazilParameters _brazilParameters, Name _name)
{
    DimensionAttribute      dimensionAttribute;

    if (_set)
    {
        dimensionAttribute                  = DimensionAttribute::findByName(_name);
        this.CostCenterDimensionAttribute   = dimensionAttribute.RecId;
    }

    return DimensionAttribute::find(this.CostCenterDimensionAttribute).Name;
}

