/// <summary>
/// Initializes the sales and purchase summary parameters for some document types.
/// </summary>
void initSummaryUpdate_BR()
{
    BrazilParameters::enableSalesSummaryParameters_BR(DocumentStatus::Quotation);
    BrazilParameters::enableSalesSummaryParameters_BR(DocumentStatus::Confirmation);
    BrazilParameters::enableSalesSummaryParameters_BR(DocumentStatus::PickingList);
    BrazilParameters::enableSalesSummaryParameters_BR(DocumentStatus::PackingSlip);
    BrazilParameters::enableSalesSummaryParameters_BR(DocumentStatus::Invoice);

    BrazilParameters::enablePurchSummaryParameters_BR(DocumentStatus::Quotation);
    BrazilParameters::enablePurchSummaryParameters_BR(DocumentStatus::Confirmation);
    BrazilParameters::enablePurchSummaryParameters_BR(DocumentStatus::PickingList);
    BrazilParameters::enablePurchSummaryParameters_BR(DocumentStatus::PackingSlip);
    BrazilParameters::enablePurchSummaryParameters_BR(DocumentStatus::Invoice);
}

public void initValue()
{
    super();

    this.FBGeneralLedgerIntegration = FBGeneralLedgerIntegration_BR::Active;
    this.ICMSOverrideICMSSTTaxationCode = NoYes::Yes;
    this.ProdEnableResetStatus = NoYes::No;

    TaxationCodeSetup_BR::updateTaxTypeList();
}

/// <summary>
/// Performs any necessary initialization or validation when the given field is modified
/// </summary>
/// <param name="_fieldId">
/// The field which was modified
/// </param>
public void modifiedField(fieldId _fieldId)
{
    super(_fieldId);

    switch (_fieldId)
    {
        case fieldnum(BrazilParameters, PurchPrimaryMethod_BR) :
            if (this.PurchPrimaryMethod_BR != PrimaryMethod_BR::Other)
            {
                this.PurchDescPrimaryMethod_BR = '';
            }

            break;

        case fieldnum(BrazilParameters, SalesPrimaryMethod_BR) :
            if (this.SalesPrimaryMethod_BR != PrimaryMethod_BR::Other)
            {
                this.SalesDescPrimaryMethod_BR = '';
            }

            break;

        case fieldNum(BrazilParameters, ProdEnableResetStatus):
            if (this.ProdEnableResetStatus)
            {
                warning("@SpedFiscalBlockK:ProdEnableParametersValidation");
            }
            break;

        case fieldNum(BrazilParameters, ProdEnableDeleteProdTable):
            if (this.ProdEnableDeleteProdTable)
            {
                warning("@SpedFiscalBlockK:ProdEnableParametersValidation");
            }
            break;
    }
}

void update()
{
    super();
    flush BrazilParameters;

    //<GBR>
    this.initSummaryUpdate_BR();
    ///GBR>
}

/// <summary>
/// Validates the DocumentType
/// </summary>
/// <param name="_oldDocuTypeId">
/// An old <c>DocuTypeId</c>
/// </param>
/// <param name="_newDocuTypeId">
/// A new <c>DocuTypeId</c>
/// </param>
/// <returns>
/// True if the old and new <c>DocuTypeId</c> are valid; otherwise, false
/// </returns>
public boolean validateDocuTypeId(  DocuTypeId  _oldDocuTypeId,
                                    DocuTypeId  _newDocuTypeId)
{
    DocuRef         docuRef;
    DocuRefExt_BR   docuRefExt;
    boolean         ret = true;

    if (_oldDocuTypeId == _newDocuTypeId)
    {
        return true;
    }

    select firstOnly RecId from docuRef
        where docuRef.TypeId == _oldDocuTypeId
    exists join docuRefExt
        where docuRefExt.DocuRef == docuRef.RecId;

    if (docuRef.RecId)
    {
        ret = checkFailed(strFmt("@GLS64287", _oldDocuTypeId));
    }

    select firstOnly RecId from docuRef
        where docuRef.TypeId == _newDocuTypeId
    notexists join docuRefExt
        where docuRefExt.DocuRef == docuRef.RecId;

    if (docuRef.RecId)
    {
        ret = checkFailed(strFmt("@GLS64288", _newDocuTypeId));
    }

    return ret;
}

public boolean validateField(fieldId _fieldIdToCheck)
{
    boolean                 ret;
    TaxTable                taxTable;
    TaxationCodeTable_BR    taxationCodeTable;

    void validateLinkedFiscalDocumentModel02TaxCode(TaxCode _taxCode, TaxType_BR _taxType)
    {
        if (_taxCode)
        {
            taxTable = TaxTable::find(_taxCode);
            if (taxTable.TaxType_BR != _taxType)
            {
                ret = checkFailed(strFmt("@GLS220231", enum2Value(_taxType)));
            }
            taxationCodeTable = TaxationCodeTable_BR::findRecId(taxTable.TaxationCodeTable_BR);
            if (taxationCodeTable.FiscalValue != TaxFiscalValue_BR::WithoutCreditDebitExempt
                && taxationCodeTable.FiscalValue != TaxFiscalValue_BR::WithoutCreditDebitOther)
            {
                ret = checkFailed(strFmt("@SYS71901", _taxCode));
            }
        }
    }

    ret = super(_fieldIdToCheck);

    switch (_fieldIdToCheck)
    {
        case fieldNum(BrazilParameters, FiscalDocumentTextDocuTypeId):
            ret = this.validateDocuTypeId(this.orig().FiscalDocumentTextDocuTypeId, this.FiscalDocumentTextDocuTypeId);
            break;

        case fieldNum(BrazilParameters, TaxTransferPIS):
            if (this.TaxTransferPIS)
            {
                taxTable = TaxTable::find(this.TaxTransferPIS);
                if (taxTable.TaxType_BR != TaxType_BR::PIS)
                {
                    ret = checkFailed(strFmt("@GLS220231", enum2Value(TaxType_BR::PIS)));
                }
            }
            break;

        case fieldNum(BrazilParameters, ComplementaryInvoicePIS):
            if (this.ComplementaryInvoicePIS)
            {
                taxTable = TaxTable::find(this.ComplementaryInvoicePIS);
                if (taxTable.TaxType_BR != TaxType_BR::PIS)
                {
                    ret = checkFailed(strFmt("@GLS220231", enum2Value(TaxType_BR::PIS)));
                }
            }
            break;

        case fieldNum(BrazilParameters, TaxTransferCOFINS):
            if (this.TaxTransferCOFINS)
            {
                taxTable = TaxTable::find(this.TaxTransferCOFINS);
                if (taxTable.TaxType_BR != TaxType_BR::COFINS)
                {
                    ret = checkFailed(strFmt("@GLS220231", enum2Value(TaxType_BR::COFINS)));
                }
            }
            break;

        case fieldNum(BrazilParameters, ComplementaryInvoiceCOFINS):
            if (this.ComplementaryInvoiceCOFINS)
            {
                taxTable = TaxTable::find(this.ComplementaryInvoiceCOFINS);
                if (taxTable.TaxType_BR != TaxType_BR::COFINS)
                {
                    ret = checkFailed(strFmt("@GLS220231", enum2Value(TaxType_BR::COFINS)));
                }
            }
            break;

        case fieldNum(BrazilParameters, LinkedFiscalDocumentModel02PIS):
            validateLinkedFiscalDocumentModel02TaxCode(this.LinkedFiscalDocumentModel02PIS, TaxType_BR::PIS);
            break;

        case fieldNum(BrazilParameters, LinkedFiscalDocumentModel02COFINS):
            validateLinkedFiscalDocumentModel02TaxCode(this.LinkedFiscalDocumentModel02COFINS, TaxType_BR::COFINS);
            break;

        case fieldNum(BrazilParameters, LinkedFiscalDocumentModel02ICMS):
            validateLinkedFiscalDocumentModel02TaxCode(this.LinkedFiscalDocumentModel02ICMS, TaxType_BR::ICMS);
            break;
    }

    return ret;
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (this.SalesPrimaryMethod_BR == PrimaryMethod_BR::Other
        && !this.SalesDescPrimaryMethod_BR)
    {
        ret = checkFailed(strfmt("@GLS50957",
                                    fieldpname(BrazilParameters, SalesPrimaryMethod_BR),
                                    enum2Value(PrimaryMethod_BR::Other),
                                    fieldpname(BrazilParameters, SalesDescPrimaryMethod_BR)));
    }

    if (this.PurchPrimaryMethod_BR == PrimaryMethod_BR::Other
        && !this.PurchDescPrimaryMethod_BR)
    {
        ret = checkFailed(strfmt("@GLS50957",
                                    fieldpname(BrazilParameters, PurchPrimaryMethod_BR),
                                    enum2Value(PrimaryMethod_BR::Other),
                                    fieldpname(BrazilParameters, PurchDescPrimaryMethod_BR)));
    }

    if (this.ICMSOverrideICMSSTTaxationCode == false && this.orig().ICMSOverrideICMSSTTaxationCode == true)
    {
        ret = checkFailed("@SYP4861206");
    }

    return ret;
}

/// <summary>
/// Enables the update parameters for a purchase summary for a given document type.
/// </summary>
/// <param name="_documentStatus">
/// The type of the document to enable the parameters for.
/// </param>
static void enablePurchSummaryParameters_BR(DocumentStatus _documentStatus)
{
    PurchSummaryParameters  purchSummaryParameters;
    container               mandatorySummaryFieldIds;
    int                     pos = 1;

    mandatorySummaryFieldIds = PurchSummaryParameters::mandatorySummaryFieldIds();

    ttsbegin;

    while (pos <= conlen(mandatorySummaryFieldIds))
    {
        if (!PurchSummaryParameters::find(_documentStatus,conpeek(mandatorySummaryFieldIds, pos)))
        {
            purchSummaryParameters.clear();
            purchSummaryParameters.initValue();
            purchSummaryParameters.DocumentStatus   = _documentStatus;
            purchSummaryParameters.FieldId          = conpeek(mandatorySummaryFieldIds, pos);
            purchSummaryParameters.insert();
        }
        pos++;
    }

    ttscommit;
}

/// <summary>
/// Enables the update parameters for a sales summary for a given document type.
/// </summary>
/// <param name="_documentStatus">
/// The type of the document to enable the parameters for.
/// </param>
static void enableSalesSummaryParameters_BR(DocumentStatus _documentStatus)
{
    SalesSummaryParameters  salesSummaryParameters;
    container               mandatorySummaryFieldIds;
    int                     pos = 1;

    mandatorySummaryFieldIds = SalesSummaryParameters::mandatorySummaryFieldIds();

    ttsbegin;

    while (pos <= conlen(mandatorySummaryFieldIds))
    {
        if (!SalesSummaryParameters::find(_documentStatus,conpeek(mandatorySummaryFieldIds, pos)))
        {
            salesSummaryParameters.clear();
            salesSummaryParameters.initValue();
            salesSummaryParameters.DocumentStatus   = _documentStatus;
            salesSummaryParameters.FieldId          = conpeek(mandatorySummaryFieldIds, pos);
            salesSummaryParameters.insert();
        }
        pos++;
    }

    ttscommit;
}

static BrazilParameters find(boolean _forupdate = false)
{
    BrazilParameters    parameter;
    ;

    parameter.selectForUpdate(_forupdate);

    select firstonly parameter
        index Key
        where parameter.Key == 0;

    if (!parameter && !parameter.isTmp())
    {
        Company::createParameter(parameter);
    }

    return parameter;
}

