/// <summary>
/// Creates a lookup on <c>DimensionAttribute</c> table
/// </summary>
/// <param name="_control">
/// A <c>FormStringControl</c> object
/// </param>
public static void lookupDimensionAttributes(FormStringControl _control)
{
    Query                   query   = new Query();
    QueryBuildDataSource    qbds;
    QueryBuildRange         queryBuildRange;

    SysTableLookup  sysTableLookup = SysTableLookup::newParameters(tableNum(DimensionAttribute), _control);

    sysTableLookup.addLookupfield(fieldNum(DimensionAttribute, Name));

    qbds = query.addDataSource(tableNum(DimensionAttribute));

    queryBuildRange = qbds.addRange(fieldNum(DimensionAttribute, Type));

    queryBuildRange.value(strFmt('(%1 == %2) || (%1 == %3)',
    fieldStr(DimensionAttribute, Type),
    any2int(DimensionAttributeType::CustomList),
    any2int(DimensionAttributeType::ExistingList)));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

/// <summary>
/// Creates a lookup on <c>DimensionAttribute</c> table to enable the selection of the fiscal establishment dimension.
/// </summary>
/// <param name="_control">
/// A <c>FormStringControl</c> object
/// </param>
public static void lookupFEDimensionAttributes(FormStringControl _control)
{
    Query                   query   = new Query();
    QueryBuildDataSource    qbds;

    SysTableLookup  sysTableLookup = SysTableLookup::newParameters(tableNum(DimensionAttribute), _control);

    sysTableLookup.addLookupfield(fieldNum(DimensionAttribute, Name));

    qbds = query.addDataSource(tableNum(DimensionAttribute));
    qbds.addRange(fieldNum(DimensionAttribute, BackingEntityTableName)).value(SysQuery::value(tableStr(FiscalEstablishment_BR)));
    qbds.addRange(fieldNum(DimensionAttribute, BackingEntityTableName)).value(SysQuery::value(tableStr(DimensionFinancialTag)));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

/// <summary>
/// Creates a lookup on <c>TaxTable</c> table filtered by a given tax type.
/// </summary>
/// <param name="_control">
/// The <c>FormStringControl</c> object to create the lookup on.
/// </param>
/// <param name="_taxType">
/// The <c>TaxType_BR</c> to be used to filter the records shown on the lookup.
/// </param>
public static void lookupLinkedFiscalDocumentModel02Tax(FormStringControl _control, TaxType_BR _taxType)
{
    Query                   query   = new Query();
    QueryBuildDataSource    qbdsTaxTable, qbdsTaxationCodeTable;

    SysTableLookup  sysTableLookup = SysTableLookup::newParameters(tableNum(TaxTable), _control);

    sysTableLookup.addLookupfield(fieldNum(TaxTable, TaxCode));
    sysTableLookup.addLookupfield(fieldNum(TaxTable, TaxName));
    sysTableLookup.addLookupfield(fieldNum(TaxTable, TaxationCodeTable_BR));

    qbdsTaxTable = query.addDataSource(tableNum(TaxTable));
    qbdsTaxationCodeTable = qbdsTaxTable.addDataSource(tableNum(TaxationCodeTable_BR) ,tableStr(TaxationCodeTable_BR));
    qbdsTaxationCodeTable.relations(true);

    qbdsTaxTable.addRange(fieldNum(TaxTable, TaxType_BR)).value(enum2str(_taxType));
    qbdsTaxationCodeTable.addRange(fieldNum(TaxationCodeTable_BR, FiscalValue)).value(strFmt('((%1 == %2) || (%1 == %3))',
            fieldStr(TaxationCodeTable_BR, FiscalValue),
            enum2int(TaxFiscalValue_BR::WithoutCreditDebitExempt),
            enum2int(TaxFiscalValue_BR::WithoutCreditDebitOther)));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

[SubscribesTo(classstr(DocuEvent), delegatestr(DocuEvent, checkSkipDocuType))]
public static void checkSkipDocuType(DocuEvent _event, DocuType _docuType)
{
    // <GBR>
    if (!_event.parmBooleanValue() && BrazilParameters::isEnabled()
        && BrazilParameters::find().FiscalDocumentTextDocuTypeId
        && _docuType.TypeId == BrazilParameters::find().FiscalDocumentTextDocuTypeId)
    {
        _event.parmBooleanValue(true);
    }
    // </GBR>
}

[SubscribesTo(classstr(DocuEvent), delegatestr(DocuEvent, onDocuViewDocuRefDataSourceInit))]
public static void dsInitResponse(Query _query, QueryBuildDataSource _qbds)
{
    QueryBuildRange             criteriaTypeId_BR;

    if (BrazilParameters::isEnabled()
        && BrazilParameters::find().FiscalDocumentTextDocuTypeId)
    {
        criteriaTypeId_BR   = _qbds.addRange(fieldnum(DocuRef,@TypeId));
        criteriaTypeId_BR.status(RangeStatus::Locked);
    }
}

[SubscribesTo(classstr(DocuEvent), delegatestr(DocuEvent, onDocuViewDocuRefDataSourceExecuteQuery))]
public static void dsQueryResponse(Query _query, QueryBuildDataSource _qbds)
{
    QueryBuildRange             criteriaTypeId_BR;
    boolean found = false;
    int i;

    for (i = 1; i <= _qbds.rangeCount(); i++)
    {
        criteriaTypeId_BR = _qbds.range(i);
        if (criteriaTypeId_BR.table() == tablenum(DocuRef) && criteriaTypeId_BR.field() == fieldnum(DocuRef,@TypeId))
        {
            found = true;
            break;
        }
    }

    if (found && BrazilParameters::isEnabled() && BrazilParameters::find().FiscalDocumentTextDocuTypeId)
    {
        criteriaTypeId_BR.value(SysQuery::valueNot(BrazilParameters::find().FiscalDocumentTextDocuTypeId));
    }
}

/// <summary>
/// Checks if Fiscal Books general ledger integration is activated.
/// </summary>
/// <param name="_result">Result of check. True if activated; otherwise, false. Instance of <c>EventHandlerResult</c> class.</param>
[SubscribesTo(tableStr(FBGeneralAdjustmentCode_BR), staticDelegateStr(FBGeneralAdjustmentCode_BR, isFBLedgerIntegrationActivated))]
public static void FBGeneralAdjustmentCode_BR_isFBLedgerIntegrationActivated(EventHandlerResult _result)
{
    if (!_result)
    {
        _result = new EventHandlerResult();
    }

    _result.booleanResult(BrazilParameters::isFBLedgerIntegrationActivated());
}

/// <summary>
/// Extracts the Brazilian parameters for PIS and COFINS taxes.
/// </summary>
/// <param name = "_result">
/// The container with four TaxValues for PIS and COFINS taxes in Cumulative and Non-Cumulative regimes.
/// </param>
/// <remarks>
/// The order of values in the result conatiner: BasicTaxValuePISCumulative, BasicTaxValuePISNonCumulative, BasicTaxValueCOFINSCumulative, BasicTaxValueCOFINSNonCumulative.
/// </remarks>
[SubscribesTo(classStr(FBTaxAssessmentCalculatorPISCOFINS_BR), staticDelegateStr(FBTaxAssessmentCalculatorPISCOFINS_BR, basicTaxValuePISCOFINS_BR))]
public static void basicTaxValuePISCOFINSHandler(EventHandlerResult _result)
{
    BrazilParameters parameters = BrazilParameters::find();

    container basicTaxValuePISCOFINS = [parameters.BasicTaxValuePISCumulative, parameters.BasicTaxValuePISNonCumulative, parameters.BasicTaxValueCOFINSCumulative, parameters.BasicTaxValueCOFINSNonCumulative];

    _result.result(basicTaxValuePISCOFINS);
}
