/// <summary>
/// Determines whether the specified record in the <c>BudgetPlanLine</c> table exists.
/// </summary>
/// <param name='_budgetPlanLineRecId'>
/// The record ID of the <c>BudgetPlanLine</c> record.
/// </param>
/// <returns>
/// true if the record was found; otherwise, false.
/// </returns>
public static boolean exist(BudgetPlanLineRecId _budgetPlanLineRecId)
{
    return (BudgetPlanLine::find(_budgetPlanLineRecId).RecId != 0);
}

/// <summary>
/// Indicates whether a specified record exists in the <c>BudgetPlanLine</c> table.
/// </summary>
/// <param name="_budgetPlanScenarioRecId">
/// The record ID of the record to check for existence.
/// </param>
/// <returns>
/// true if the specified record exists; otherwise, false.
/// </returns>
public static boolean existsForScenario(
    BudgetPlanScenarioRecId _budgetPlanScenarioRecId)
{
    BudgetPlanLine  budgetPlanLine;
    boolean         isExist;

    if (_budgetPlanScenarioRecId)
    {
        isExist = (select firstOnly budgetPlanLine
                    where budgetPlanLine.BudgetPlanScenario == _budgetPlanScenarioRecId).RecId != 0;
    }

    return isExist;
}

/// <summary>
/// Finds a record in the <c>BudgetPlanLine</c> table.
/// </summary>
/// <param name='_budgetPlanLineRecId'>
/// The record ID of the <c>BudgetPlanLine</c> record.
/// </param>
/// <param name='_forUpdate'>
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name='_concurrencyModel'>
/// The concurrency model option to use when the record is read; optional.
/// </param>
/// <returns>
/// A record in the <c>BudgetPlanLine</c> table; otherwise, an empty record.
/// </returns>
public static BudgetPlanLine find(
    BudgetPlanLineRecId _budgetPlanLineRecId,
    boolean             _forUpdate = false,
    ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    BudgetPlanLine budgetPlanLine;

    if (_budgetPlanLineRecId)
    {
        budgetPlanLine.selectForUpdate(_forUpdate);

        if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
        {
            budgetPlanLine.concurrencyModel(_concurrencyModel);
        }

        select firstOnly budgetPlanLine
            where budgetPlanLine.RecId == _budgetPlanLineRecId;
    }

    return budgetPlanLine;
}

/// <summary>
/// Finds the specified record in the <c>BudgetPlanLine</c> table that is based on the
/// given <c>BudgetPlanHeader</c> table record ID and <c>BudgetPlanScenario</c> table record ID.
/// </summary>
/// <param name='_budgetPlanHeaderRecId'>
/// The record ID of the <c>BudgetPlanHeader</c> table.
/// </param>
/// <param name='_budgetPlanScenarioRecId'>
/// The record ID of the <c>BudgetPlanScenario</c> table.
/// </param>
/// <param name='_forUpdate'>
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name='_concurrencyModel'>
/// The concurrency model option to use when the record is read; optional.
/// </param>
/// <returns>
/// A record in the <c>BudgetPlanLine</c> table; otherwise, an empty record.
/// </returns>
public static BudgetPlanLine findByPlanHeaderScenario(
    BudgetPlanHeaderRecId    _budgetPlanHeaderRecId,
    BudgetPlanScenarioRecId  _budgetPlanScenarioRecId,
    boolean                  _forUpdate = false,
    ConcurrencyModel         _concurrencyModel = ConcurrencyModel::Auto)
{
    BudgetPlanLine budgetPlanLine;

    if (_budgetPlanHeaderRecId
        && _budgetPlanScenarioRecId)
    {
        budgetPlanLine.selectForUpdate(_forUpdate);

        if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
        {
            budgetPlanLine.concurrencyModel(_concurrencyModel);
        }

        select firstOnly budgetPlanLine
            where budgetPlanLine.BudgetPlanHeader   == _budgetPlanHeaderRecId
                && budgetPlanLine.BudgetPlanScenario == _budgetPlanScenarioRecId;
    }

    return budgetPlanLine;
}

/// <summary>
/// Finds the specified record in the <c>BudgetPlanLine</c> table that is based on the
/// given <c>BudgetPlanHeader</c> record ID, <c>BudgetPlanScenario</c> record ID and <c>ScenarioLineNumber</c> value.
/// </summary>
/// <param name='_budgetPlanHeaderRecId'>
/// The record ID of the <c>BudgetPlanHeader</c> record.
/// </param>
/// <param name='_budgetPlanScenarioRecId'>
/// The record ID of the <c>BudgetPlanScenario</c> record.
/// </param>
/// <param name='_scenarioLineNumber'>
/// The scenario line number of the <c>BudgetPlanLine</c> record.
/// </param>
/// <param name='_forUpdate'>
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name='_concurrencyModel'>
/// The concurrency model option to use when the record is read; optional.
/// </param>
/// <returns>
/// A record in the <c>BudgetPlanLine</c> table; otherwise, an empty record.
/// </returns>
public static BudgetPlanLine findByPlanHeaderScenarioLineNumber(
    BudgetPlanHeaderRecId    _budgetPlanHeaderRecId,
    BudgetPlanScenarioRecId  _budgetPlanScenarioRecId,
    guid                     _scenarioLineNumber,
    boolean                  _forUpdate = false,
    ConcurrencyModel         _concurrencyModel = ConcurrencyModel::Auto)
{
    BudgetPlanLine budgetPlanLine;

    if (_budgetPlanHeaderRecId
        && _budgetPlanScenarioRecId
        && _scenarioLineNumber)
    {
        budgetPlanLine.selectForUpdate(_forUpdate);

        if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
        {
            budgetPlanLine.concurrencyModel(_concurrencyModel);
        }

        select firstOnly budgetPlanLine
            where budgetPlanLine.BudgetPlanHeader    == _budgetPlanHeaderRecId
                && budgetPlanLine.BudgetPlanScenario == _budgetPlanScenarioRecId
                && budgetPlanLine.LineReferenceId    == _scenarioLineNumber;
    }

    return budgetPlanLine;
}

/// <summary>
/// Finds a record in the <c>BudgetPlanLine</c> table.
/// </summary>
/// <param name='_budgetPlanHeaderRecId'>
/// The record ID of the <c>BudgetPlanHeader</c> record.
/// </param>
/// <param name='_ledgerDimension'>
/// The record ID of the <c>DimensionAttributeValueCombination</c> record.
/// </param>
/// <param name='_effectiveDate'>
/// The effective date of the <c>BudgetPlanLine</c> record.
/// </param>
/// <returns>
/// A record in the <c>BudgetPlanLine</c> table; otherwise, an empty record.
/// </returns>
public static BudgetPlanLine findFirstByHeaderLedgerDimensionDate(
    BudgetPlanHeaderRecId       _budgetPlanHeaderRecId,
    LedgerDimensionAccount      _ledgerDimension,
    BudgetPlanLineEffectiveDate _effectiveDate)
{
    BudgetPlanLine budgetPlanLine;

    if (_budgetPlanHeaderRecId
        && _ledgerDimension
        && _effectiveDate)
    {
        select firstOnly budgetPlanLine
            where budgetPlanLine.BudgetPlanHeader == _budgetPlanHeaderRecId
               && budgetPlanLine.LedgerDimension  == _ledgerDimension
               && budgetPlanLine.EffectiveDate    == _effectiveDate;
    }

    return budgetPlanLine;
}

/// <summary>
/// Deletes all record in the <c>BudgetPlanLine</c> table and related tables due to delete of a <c>BudgetPlanLineActiveView</c> record.
/// </summary>
/// <param name='_budgetPlanActiveView'> The record ID of the <c>BudgetPlanLineActiveView</c> record.</param>
public static void deleteLinesByActiveView(BudgetPlanLineActiveViewRecId _budgetPlanActiveView)
{
    BudgetPlanLine                  budgetPlanLine;
    BudgetPlanLineActiveViewDetail  budgetPlanActiveViewDetail;

    BudgetPlanLineBudgetTransactionLineLink transactionLineLink;

    budgetPlanLine.skipDeleteActions(true);

    ttsbegin;

    delete_from transactionLineLink
        exists join budgetPlanActiveViewDetail
            where budgetPlanActiveViewDetail.Line == transactionLineLink.BudgetPlanLine
                && budgetPlanActiveViewDetail.ActiveView == _budgetPlanActiveView;

    delete_from budgetPlanLine
        exists join budgetPlanActiveViewDetail
            where budgetPlanActiveViewDetail.Line == budgetPlanLine.RecId
                && budgetPlanActiveViewDetail.ActiveView == _budgetPlanActiveView;
    ttscommit;
}
