AmountCur amountCur2Euro(AmountCur _amountCur)
{
    CurrencyCode euroCurrencyCode;

    euroCurrencyCode = Currency::findEuroCurrencyCode();

    if (euroCurrencyCode == '')
    {
        return 0;
    }

    return CurrencyExchangeHelper::curAmount(CurrencyExchangeHelper::mstAmount(_amountCur, this.CurrencyCode, this.ConfirmDate, Currency::noYes2UnknownNoYes(this.Triangulation), this.ExchRate, this.ExchRateSecondary), euroCurrencyCode, this.ConfirmDate);
}

