void  initFromSalesTable(SalesTable  salesTable)
{
    this.SalesId                = salesTable.SalesId;
    this.OrderAccount           = salesTable.CustAccount;
    this.InvoiceAccount         = salesTable.InvoiceAccount;

    this.DeliveryName           = salesTable.DeliveryName ;
    this.DeliveryPostalAddress  = salesTable.DeliveryPostalAddress;

    this.PurchaseOrder          = salesTable.PurchOrderFormNum;
    this.CustomerRef            = salesTable.CustomerRef;
    this.DlvTerm                = salesTable.DlvTerm;
    this.DlvMode                = salesTable.DlvMode;
    this.CustGroup              = salesTable.CustGroup;
    this.WorkerSalesTaker       = salesTable.WorkerSalesTaker;
    this.LanguageId             = salesTable.LanguageId;
    this.Payment                = salesTable.Payment;
    this.FixedDueDate           = salesTable.FixedDueDate;
    this.CashDiscCode           = salesTable.CashDisc;

    if (TaxParameters::find().CashDiscOnInvoice)
    {
        GlobalizationInstrumentationHelper::featureRun(GlobalizationConstants::FeatureReferenceES00021, funcName());
        this.CashDiscPercent    = salesTable.CashDiscPercent;
    }

    this.InclTax                = salesTable.InclTax;

    this.Qty                    = 0;
    this.Volume                 = 0;
    this.Weight                 = 0;
    this.CostValue              = 0;
    this.SumLineDisc            = 0;
    this.SumMarkup              = 0;
    this.SalesBalance           = 0;
    this.EndDisc                = 0;
    this.RoundOff               = 0;
    this.ConfirmAmount          = 0;
    this.CurrencyCode           = salesTable.CurrencyCode;

    this.Triangulation          = salesTable.euroTriangulation(this.ConfirmDate);
    this.ExchRate               = salesTable.exchRate(this.ConfirmDate);
    this.ExchRateSecondary      = salesTable.exchRateSecondary(this.ConfirmDate);
    this.DefaultDimension       = this.copyDimension(salesTable.DefaultDimension);
    this.Deadline               = salesTable.Deadline;
}

public void initValue()
{
    super();
}

public void insert()
{
    super();

    WHSJourLoadTable::insertJourLoadTable(this.SalesId,
                                          this.ConfirmId,
                                          WHSCustJourType::Confirmation,
                                          this.ParmId);

    if (!this.isProforma())
        MCRHoldCodeTrans::insertHoldCodeTrans(this);
}

public boolean isProforma()
{
    return !this.ConfirmId;
}

public boolean isSummaryUpdated()
{
    Query       query = this.queryCustConfirmSalesLink();
    QueryRun    queryRun;

    query.dataSourceTable(tableNum(CustConfirmSalesLink)).firstOnly(true);
    query.dataSourceTable(tableNum(CustConfirmSalesLink)).addRange(fieldNum(CustConfirmSalesLink, OrigSalesId)).value(strFmt('!%1',queryValue(this.SalesId)));

    queryRun = new QueryRun(query);

    return queryRun.next();
}

/// <summary>
/// Retrieves a dimension set that holds the merged combination of the current <c>defaultDimension</c>
/// field on this table and the provided dimension set.
/// </summary>
/// <param name="_primaryDefaultDimension">
/// A first dimension set to merge with the current <c>defaultDimension</c> field on this table.
/// </param>
/// <param name="_secondaryDefaultDimension">
/// A second dimension set to merge with the current <c>defaultDimension</c> field on this table;
/// optional.
/// </param>
/// <param name="_dimensionMerge">
/// A <c>dimensionMerge</c> object that was previously initialized using the current buffer; optional.
/// </param>
/// <returns>
/// A dimension set that holds the merged combination of the current <c>defaultDimension</c> field on
/// this table and the provided dimension set.
/// </returns>
/// <remarks>
/// This method makes sure that potential linked dimensions are not overwritten when they are merged.
/// </remarks>

public DimensionDefault mergeDimension(
    DimensionDefault _primaryDefaultDimension,
    DimensionDefault _secondaryDefaultDimension = 0,
    DimensionMerge   _dimensionMerge = DimensionMerge::newFromTable(this,
                                                                    this.companyInfo().RecId
                                                                    )
    )
{
    return _dimensionMerge.merge(_primaryDefaultDimension, _secondaryDefaultDimension);
}

