}

static boolean  exist(SalesId  salesId)
{
    return salesId && (select custConfirmJour
            where custConfirmJour.SalesId == salesId
           ).RecId != 0;
}

static CustConfirmJour findFromCustConfirmTrans(SalesId         salesId,
                                                ConfirmId       confirmId,
                                                TransDate       confirmDate,
                                                boolean         update = false)
{
    CustConfirmJour  custConfirmJour;

    if (confirmId)
    {
        custConfirmJour.selectForUpdate(update);

        select firstonly custConfirmJour
            where custConfirmJour.SalesId       == salesId
               && custConfirmJour.ConfirmId     == confirmId
               && custConfirmJour.ConfirmDate   == confirmDate;
    }

    return custConfirmJour;
}

static CustConfirmJour findRecId(recId recId, boolean update = false)
{
    CustConfirmJour custConfirmJour;

    custConfirmJour.selectForUpdate(update);

    select firstonly custConfirmJour
            index hint recid
        where custConfirmJour.RecId     == recId;

    return custConfirmJour;
}

static int  numberOfConfirm(SalesTable  source)
{
    CustConfirmJour  custConfirmJour;

    select count (RecId) from custConfirmJour
        index hint SalesIdx
        where custConfirmJour.SalesId == source.SalesId;

    return any2int(custConfirmJour.RecId);
}
