{
    TaxModelDocLineCustInvoiceImpl_IN taxModelDocLine;

    taxModelDocLine = TaxModelDocLineFactory::newTaxModelDocLine(this) as TaxModelDocLineCustInvoiceImpl_IN;

    return taxModelDocLine;
}

