boolean freeInvoiceCheckCreditOnHeader()
{
    return this.CreditLimitCheckFreeInvoice == NoYes::Yes && this.CreditMaxCheck != TypeOfCreditmaxCheck::None;
}

public void initValue()
{
    super();

    this.CashDiscForCreditNote = NoYes::Yes;

    if (CustParameters::regionRequiresSequentialLineNumbersWhenPostingCustomerInvoiceLines()
        || !CustInvoiceTransRandLineCreationSeqNumFlight::instance().isEnabled())
    {
        this.UseSequentialLineNumbersWhenPostingCustomerInvoiceLines = NoYes::Yes;
    }
    else
    {
        this.UseSequentialLineNumbersWhenPostingCustomerInvoiceLines = NoYes::No;
    }

    this.PercentCustomersPerBatchTask = 10;

    // SEPA Direct Debit Mandate defaults
    this.MandateDefaultExpirationMonths = 36;
    this.PrenotificationDaysFirst = 14;
    this.PrenotificationDaysRecurring = 14;
    this.CoreSchemeBankSubmissionDaysFirst = 5;
    this.CoreSchemeBankSubmissionDaysRecurring = 2;
    this.b2bSchemeBankSubmissionDaysFirst = 1;
    this.b2bSchemeBankSubmissionDaysRecurring = 1;
    this.Cor1SchemeBankSubmissionDaysFirst = 1;
    this.Cor1SchemeBankSubmissionDaysRecurring = 1;
    this.CreditCardPreapprovalLastNoOfDays = 7;
    this.CreditCardAuthTransArchiveThreshold = 25000;
    this.CreditCardCustPurgeThreshold = 25000;
    this.DefaultFreeTextInvoicePostingTopPickBatchTasks = 8;


    if (this.CustAgingDeleteChunkSize <= 0)
    {
        this.CustAgingDeleteChunkSize = 100000;
    }
}

