public void modifiedField(FieldId _fieldId)
{
    super(_fieldId);

    switch (_fieldId)
    {
        case fieldNum(CustParameters, MaxMSTDiff) :
            if (this.MaxMSTOverUnder < this.MaxMSTDiff)
            {
                this.MaxMSTOverUnder = this.MaxMSTDiff;
            }
            break;

        case fieldNum(CustParameters, CreditCardPostingAccountType):
            this.CreditCardPostingLedgerDimension = 0;
            break;

        case fieldNum(CustParameters, EnableProject):
            if (!this.EnableProject)
            {
                this.ProjLedgerAllowEdit = NoYes::No;
            }
            break;

        case fieldNum(CustParameters, EntryCertificateManagementEnabled_W) :
            if (this.EntryCertificateManagementEnabled_W == NoYes::No)
            {
                this.EntryCertificateIssuingEnabled_W = NoYes::No;
            }
            break;

        case fieldnum(CustParameters, EnableReferToCollectionAgency_W) :
            if (this.EnableReferToCollectionAgency_W == NoYes::No)
            {
                this.CollectionAgencyTaxRate_W = 0;
                this.CollectionGracePeriodForDuesTransfer_W = 0;
            }
            break;

        case fieldnum(CustParameters, EnableChronologicalInvoiceNumbering_W) :
            if (   this.EnableChronologicalInvoiceNumbering_W == NoYes::No
                && this.orig().EnableChronologicalInvoiceNumbering_W == NoYes::Yes)
            {
                DialogButton res = Box::yesNo("@AccountsReceivable:ChronologicalSetupDisablingConfirmation",
                                              DialogButton::No,
                                              "@SYS28003");

                if (res == DialogButton::No)
                {
                    this.EnableChronologicalInvoiceNumbering_W = this.orig().EnableChronologicalInvoiceNumbering_W;
                }
            }
        case fieldNum(CustParameters, CustAgingSnapshotBatchLimit) :
            if (this.CustAgingSnapshotBatchLimit == 0)
            {
                this.CustAgingSnapshotBatchLimit = 2;
            }
            break;

        case fieldNum(CustParameters, PercentCustomersPerBatchTask) :
            if (this.PercentCustomersPerBatchTask == 0)
            {
                this.PercentCustomersPerBatchTask = 10;
            }
            break;

        default:
            break;
    }
}

public CompanyBankAccountId parmCreditCardPostingAccount(
    CompanyBankAccountId _account = '',
    LedgerJournalACType _accountType = this.CreditCardPostingAccountType)
{
    DimensionAttributeValueCombination ledgerDimension;
    CompanyBankAccountId account;

    if (!prmisDefault(_accountType))
    {
        this.CreditCardPostingAccountType = _accountType;
    }

    // If account has been changed, update the backing ledger dimension value
    if (!prmisDefault(_account))
    {
        if (_account)
        {
            this.CreditCardPostingLedgerDimension = LedgerDynamicAccountHelper::getDynamicAccountFromAccountNumber(_account, _accountType);
            account = _account;
        }
        else
        {
            account = '';
            this.CreditCardPostingLedgerDimension = 0;
        }
    }
    else if (this.CreditCardPostingLedgerDimension)
    {
        // If the account has not been changed, then pull the value from ledger dimension as the master
        select DisplayValue from ledgerDimension
            where ledgerDimension.RecId == this.CreditCardPostingLedgerDimension;
        account = ledgerDimension.DisplayValue;
    }

    return account;
}

public DimensionDynamicDefaultAccount parmCreditCardPostingLedgerDimension(DimensionDynamicDefaultAccount _ledgerDimension = this.CreditCardPostingLedgerDimension)
{
    this.CreditCardPostingLedgerDimension = _ledgerDimension;
    return this.CreditCardPostingLedgerDimension;
}

