void update()
{
    ttsbegin;

    if (this.orig().CollectionLetterGenerationLevel != this.CollectionLetterGenerationLevel && this.isCustomerLevelCollectionLetterEnabled())
    {
        this.enableCustomerLevelCollectionLetter();
    }

    super();

    ttscommit;

    flush CustParameters;
}

