}

boolean validateField(FieldId _fieldId)
{
    #define.num1(1)
    #define.num100(100)
    #define.num999(999)
    const int MaxArchives = 100000;

    // <GEERU>
    #isoCountryRegionCodes
    // </GEERU>

    CustTrans custTrans;
    CustInvoiceTable custInvoiceTable;

    boolean ok = super(_fieldId);

    if (ok)
    {
        switch (_fieldId)
        {
            case fieldNum(CustParameters, ClearingLedgerDimension):
                ok = MainAccount::checkTransactionAccount(this.ClearingLedgerDimension);
                break;

            case fieldNum(CustParameters, InvoiceReferenceNumberSequenceTable):
                if (this.CustInvoiceRefNum_FI && this.InvoiceReferenceNumberSequenceTable)
                {
                    ok = CustInvoiceReferenceNumber_FI_Formula2::checkReferenceNumberFormat(NumberSequenceTable::find(this.InvoiceReferenceNumberSequenceTable).Format);
                }
                break;
            //credit card module
            case fieldNum(CustParameters, CreditCardPreapprovalLastNoOfDays):
                if (this.CreditCardPreapprovalLastNoOfDays > #num999 ||
                    this.CreditCardPreapprovalLastNoOfDays < #num1)
                {
                    ok = checkFailed(strFmt("@SYS116872", #num1, #num999));
                }
                break;
            case fieldNum(CustParameters, CreditCardEstShippingChargeType):
            case fieldNum(CustParameters, CreditCardEstShippingChargeValue):
                if (this.CreditCardEstShippingChargeType == CreditCardEstShippingChargeType::Percent &&
                    this.CreditCardEstShippingChargeValue > #num100)
                {
                    ok = checkFailed(strFmt("@SYS116872", 0, #num100));
                }
                break;

            case fieldNum(CustParameters, CreditCardPostingLedgerDimension):
                if (this.CreditCardPostingAccountType == LedgerJournalACType::Bank)
                {
                    if (this.parmCreditCardPostingAccount())
                    {
                        ok = BankAccountTable::checkIsActive(this.parmCreditCardPostingAccount());
                    }
                }
                break;
            // <GEERU>
            case fieldNum(CustParameters, SalesBookFactureProcessingType_RU):
                if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU])
                    && this.SalesBookFactureProcessingType_RU > SalesBookFactureProcessingType_RU::VATStandard)
                {
                    ok = checkFailed(strFmt("@GLS112967", this.SalesBookFactureProcessingType_RU));
                }
                break;
            // </GEERU>
            case fieldNum(CustParameters, EnableProject):
                if (!this.EnableProject)
                {
                    warning("@SYS314846");
                }
                break;

            case fieldNum(CustParameters, MaximumWaiveDays):
            case fieldNum(CustParameters, MinimumWaiveDays):
                if (this.MaximumWaiveDays < this.MinimumWaiveDays && this.MaximumWaiveDays && this.MinimumWaiveDays)
                {
                    ok = checkFailed("@SYS315384");
                }
                break;

            case fieldNum(CustParameters, MarkOpenInvoiceLine):
                if (this.MarkOpenInvoiceLine == NoYes::Yes && TaxParameters::find().TaxSpecifyLine == NoYes::No)
                {
                    ok = checkFailed(strFmt("@SYS344847", "@SYS21908", "@SYS71496", "@SYS134066"));
                }
                break;

            case fieldNum(CustParameters, CustUseBillingClassification_PSN):
                if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)) && this.CustUseBillingClassification_PSN == NoYes::No)
                {
                    select firstonly RecId from custInvoiceTable
                        where custInvoiceTable.CustBillingClassification != 0;

                    ok = custInvoiceTable.RecId ? checkFailed("@SYS4000267") : true;

                    if (ok)
                    {
                        select firstonly RecId from custTrans
                            where custTrans.CustBillingClassification != 0;

                        ok = custTrans.RecId ? checkFailed("@SYS4000267") : true;
                    }
                }
                break;

            case fieldNum(CustParameters, CreditCardAuthTransArchiveThreshold):
                if (this.CreditCardAuthTransArchiveThreshold > MaxArchives)
                {
                    ok = checkFailed("@AccountsReceivable:CreditCardAuthTransArchiverExcessMaxValue");
                }
                break;

            case fieldNum(CustParameters, UseSequentialLineNumbersWhenPostingCustomerInvoiceLines) :
                if (this.UseSequentialLineNumbersWhenPostingCustomerInvoiceLines == NoYes::No)
                {
                    if (CustParameters::regionRequiresSequentialLineNumbersWhenPostingCustomerInvoiceLines())
                    {
                        ok = checkFailed("@AccountsReceivable:UseSequentialLineNumbersWhenPostingCustomerInvoiceLines_ValidateFailed_Region");
                    }
                    else if (!CustInvoiceTransRandLineCreationSeqNumFlight::instance().isEnabled())
                    {
                        ok = checkFailed("@AccountsReceivable:UseSequentialLineNumbersWhenPostingCustomerInvoiceLines_ValidateFailed_Flight");
                    }
                }
                break;

            case fieldNum(CustParameters, CreditCardCustPurgeThreshold):
                if (this.CreditCardCustPurgeThreshold > MaxArchives)
                {
                    ok = checkFailed("@AccountsReceivable:CreditCardCustPurgeExcessMaxValue");
                }
                break;
        }
    }
    return ok;
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (this.CustInvoiceRefNum_FI &&
         (this.InvoiceReferenceNumberFormula_FI == InvoiceReferenceNumberFormulaType_FI::Formula2 ||
          this.InvoiceReferenceNumberFormula_FI == InvoiceReferenceNumberFormulaType_FI::NumberSequence))
    {
        if (!this.InvoiceReferenceNumberSequenceTable)
        {
            ret = checkFailed("@SYS98665");
        }
    }

    if (!this.CreditCardPostingLedgerDimension && CreditCardProcessors::activeProcessorExists())
    {
        ret = checkFailed("@AccountsReceivable:CreditCardPostingAccountFilledIn");
    }

    if (this.SettlementWriteOffEnabled && !this.SettlementWriteOffJournalName)
    {
        ret = checkFailed(strFmt("@SYS26332", "@SYS97804"));
    }

    if (FeatureStateProvider::isFeatureEnabled(CustVendExchRateTypeEnhancementFeature::instance())
        && this.ExchAdjRateSource == CustVendExchAdjRateSource::Specific
        && (this.AccountingCurrencyExchangeRateType == 0 || this.ReportingCurrencyExchangeRateType == 0))
    {
        ret = checkFailed("@CashManagement:CustVendExchRateMissingExchangeRateType");
    }

    return ret;
}

static container allowedNumSeqGroup()
{
    //<GEERU>
    #ISOCountryRegionCodes
    //</GEERU>
    // <GEERU>
    container con;
    con = [CustParameters::numRefCustInvoiceId().NumberSequenceDatatype,
           CustParameters::numRefCustCreditNoteId().NumberSequenceDatatype,
           CustParameters::numRefCustInvoiceVoucher().NumberSequenceDatatype,
           CustParameters::numRefCustCreditNoteVoucher().NumberSequenceDatatype];

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        con += [CustParameters::numRefFactureExternalId_RU().NumberSequenceDatatype,
                CustParameters::numRefCustCorrectInvoiceId_RU().NumberSequenceDatatype,
                CustParameters::numRefCustCorrectCreditNoteId_RU().NumberSequenceDatatype,
                CustParameters::numRefCustCorrectInvoiceVoucher_RU().NumberSequenceDatatype,
                CustParameters::numRefCustCorrectCreditNoteVoucher_RU().NumberSequenceDatatype,
                CustParameters::numRefOrigAsFactureCorrectExternalId_RU().NumberSequenceDatatype,
                CustParameters::numRefOrigDocAsFactureExternalId_RU().NumberSequenceDatatype,
                CustParameters::numRefCorrectFactureExternalId_RU().NumberSequenceDatatype];
    }
    // </GEERU>
    // <GTH>
    else if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoTH]))
    {
        con += [CustParameters::numRefCustDebitNoteId().NumberSequenceDatatype,
                CustParameters::numRefCustDebitNoteVoucher().NumberSequenceDatatype,
                CustParameters::numRefCustTaxInvoiceId().NumberSequenceDatatype,
                CustParameters::numRefCustTaxInvoiceVoucher().NumberSequenceDatatype];
    }
    // </GTH>
    // <GMY>
    else if (TaxGSTFeatureChecker_MY::isCountryRegionMY())
    {
        con += [CustParameters::numRefCustDebitNoteId_MY().NumberSequenceDatatype,
                CustParameters::numRefCustDebitNoteVoucher_MY().NumberSequenceDatatype];
    }
    // </GMY>
    // <GEERU>
    return con;
    // </GEERU>
}

