static boolean exist()
{
    return (select firstonly RecId from custParameters).RecId != 0;
}

static CustParameters find(boolean _forupdate = false)
{
    CustParameters parameter;

    if (_forupdate)
    {
        parameter.selectForUpdate(_forupdate);
    }

    select firstonly parameter
        index Key
        where parameter.Key == 0;

    if (!parameter && !parameter.isTmp())
    {
        Company::createParameter(parameter);
        PriceDiscSalesPolicyParameters::initParameters();
    }

    return parameter;
}

/// <summary>
/// Use UseFiscalInvoiceAccount value for Spain region, otherwise Enum value Always will be returned.
/// </summary>
/// <returns>
/// returns UseFiscalInvoiceAccount value.
/// </returns>
public UseFiscalInvoiceAccount getUseFiscalInvoiceAccount_ES()
{
    #ISOCountryRegionCodes

    if(SysCountryRegionCode::isLegalEntityInCountryRegion([#isoES])
        || EnableDefaultingOfFiscalDataFromInvoiceAccountFeature::isEnabled())
    {
        return this.UseFiscalInvoiceAccount;
    }

    return UseFiscalInvoiceAccount::Always;
}

public static CustParameters findByCompany(DataAreaId _company, boolean _forUpdate = false)
{
    CustParameters parameter;

    changecompany(_company)
    {
        parameter = CustParameters::find(_forUpdate);
    }

    return parameter;
}

