#VATTransitionPeriod_RU
public static CustTaxation_RU taxation_RU(TransDate _transDate)
{
    if (CustParameters::find().CustTaxation_RU == CustTaxation_RU::OnPayment && _transDate < #TransitionStartDate)
    {
        return CustTaxation_RU::OnPayment;
    }

    return CustTaxation_RU::OnDelivery;
}

#VATTransitionPeriod_RU
public static boolean taxationAllowEdit_RU()
{
    SalesBookTable_RU salesBookTable;

    salesBookTable = SalesBookTable_RU::getLastClosedBook();

    if ((salesBookTable.ClosingDate > #BeforeTransitionStartDate &&
         salesBookTable.ClosingDate < #TransitionEndDateOld) ||
         salesBookTable.ClosingDate > #TransitionEndDateOld)
    {
        return false;
    }

    return true;
}

